mapper.update:
	UPDATE	user_meals
    SET		meal_text = #{menu},
    		upd_id = #{login_id},
    		upd_ip = #{login_ip}
    WHERE	user_id = #{user_id}
    	AND	meal_date = #{rec_date}
    	AND meal_type = #{meal_type}

mapper.select:
	SELECT	user_id,
			meal_date as rec_date,
			meal_type,
			meal_text as menu
	FROM	user_meals
    WHERE	user_id = #{user_id}
    	AND	meal_date = #{meal_date}
    	AND meal_type = #{meal_type}
