import React from 'react';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import './css/calendarview.css'; // ✅ 스타일 분리 (선택)

const localizer = momentLocalizer(moment);

export default function CalendarView({ meals }) {
  const events = meals.map(meal => ({
    title: `${meal.meal_type_nm}: ${meal.meal_text}`,
    start: new Date(meal.meal_date),
    end: new Date(meal.meal_date),
    allDay: true,
  }));

  return (
    <div className="calendar-container"> {/* ✅ 감싸는 div */}
      <Calendar
        localizer={localizer}
        events={events}
        startAccessor="start"
        endAccessor="end"
        style={{ height: 500, minWidth: '700px' }} // ✅ 최소 폭 설정 (달력 구조상 7열 기준)
      />
    </div>
  );
}
