import React, { useState } from "react";
import axios from "axios";

const ChatTest = () => {
  const [message, setMessage] = useState("");
  const [responses, setResponses] = useState([]);

  const handleSend = async () => {
    if (!message.trim()) return;

    try {
      const userId = sessionStorage.getItem("userId"); // 로그인 세션에서 유저 id 가져오기
      console.log(userId); // 이거 찍어봐!
      const res = await axios.post("http://localhost:8000/api/rag/generate", {
        user_id: userId,
        question: message,
      });

      setResponses((prev) => [...prev, { role: "user", content: message }]);
      setResponses((prev) => [...prev, { role: "bot", content: res.data.answer }]);
      setMessage("");
    } catch (err) {
      console.error("챗봇 응답 실패:", err);
    }
  };

  return (
    <div className="max-w-xl mx-auto p-4">
      <div className="border p-4 rounded h-96 overflow-y-scroll mb-2 bg-gray-100">
        {responses.map((msg, idx) => (
          <div key={idx} className={`mb-2 ${msg.role === "user" ? "text-right" : "text-left"}`}>
            <span className={`inline-block px-3 py-1 rounded ${msg.role === "user" ? "bg-blue-200" : "bg-green-200"}`}>
              {msg.content}
            </span>
          </div>
        ))}
      </div>
      <div className="flex gap-2">
        <input
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="메시지를 입력하세요..."
          className="flex-grow border px-3 py-2 rounded"
        />
        <button onClick={handleSend} className="bg-blue-500 text-white px-4 py-2 rounded">
          전송
        </button>
      </div>
    </div>
  );
};

export default ChatTest;
