import React, { useEffect } from 'react';
import './css/kakaomap.css';

export default function KakaoMap({ stores }) {
  // 지도에 들어갈 데이터 확인
  console.log("📦 전달된 stores:", stores);
  stores.forEach(store => console.log("📍 주소 확인:", store.real_address));

  useEffect(() => {
    const loadKakaoMapScript = () => {
      if (window.kakao && window.kakao.maps) {
        initMap();
        return;
      }

      const script = document.createElement("script");
      script.src =
        "//dapi.kakao.com/v2/maps/sdk.js?appkey=cdcb36f7c5dbf63f04c19c92959118ca&autoload=false&libraries=services";
      script.async = true;
      script.onload = () => {
        window.kakao.maps.load(() => {
          initMap();
        });
      };
      document.head.appendChild(script);
    };

    const initMap = () => {
        const container = document.getElementById("map");
        const options = {
          center: new window.kakao.maps.LatLng(37.5665, 126.9780),
          level: 5,
        };
        const map = new window.kakao.maps.Map(container, options);
      
        // 테스트용 마커
        const testMarker = new window.kakao.maps.Marker({
          map: map,
          position: new window.kakao.maps.LatLng(37.5665, 126.9780),
        });
      
        testMarker.setMap(map);
      };

    loadKakaoMapScript();
  }, [stores]);

  return (
    <div
      id="map"
      style={{ width: "100%", height: "400px", marginTop: "20px" }}
    />
  );
}
