import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './css/main.css';

export default function NavBar() {
    const [menuOpen, setMenuOpen] = useState(false);
    const [userInfo, setUserInfo] = useState(null);
    const navigate = useNavigate();
    const BASE_URL = process.env.REACT_APP_BASE_URL;

    // ‚úÖ 1. localStorageÏóêÏÑú Ï¥àÍ∏∞Ìôî
    useEffect(() => {
        const storedUser = localStorage.getItem('user');
        if (storedUser) {
            try {
                const parsed = JSON.parse(storedUser);
                setUserInfo(parsed);
                console.log("‚úÖ NavBar Ï¥àÍ∏∞Ìôî - localStorage Ïú†Ï†Ä", parsed);
            } catch (err) {
                console.warn("‚ùå localStorage ÌååÏã± Ïã§Ìå®", err);
            }
        }
    }, []);

    // ‚úÖ 2. Ìï≠ÏÉÅ ÏµúÏã† ÏÑ∏ÏÖò ÎèôÍ∏∞Ìôî
    useEffect(() => {
        axios.get(`${BASE_URL}/users/ses`, { withCredentials: true })
            .then((res) => {
                if (res.data) {
                    console.log("‚úÖ ÏÑ∏ÏÖò ÎèôÍ∏∞Ìôî ÏôÑÎ£å:", res.data);
                    setUserInfo(res.data);
                    localStorage.setItem('user', JSON.stringify(res.data));
                }
            })
            .catch(() => {
                console.log('‚ùå ÏÑ∏ÏÖò ÏóÜÏùå');
                localStorage.removeItem('user');
                setUserInfo(null);
            });
    }, []);

    // üß™ userInfo Î≥ÄÍ≤Ω Î°úÍ∑∏
    useEffect(() => {
        console.log('üåÄ userInfo Î≥ÄÍ≤Ω:', userInfo);
    }, [userInfo]);

    useEffect(() => {
        document.body.style.overflow = menuOpen ? 'hidden' : '';
    }, [menuOpen]);

    useEffect(() => {
        const handleResize = () => {
            if (window.innerWidth > 768 && menuOpen) {
                setMenuOpen(false);
            }
        };
        window.addEventListener('resize', handleResize);
        return () => window.removeEventListener('resize', handleResize);
    }, [menuOpen]);

    // Î°úÍ∑∏ÏïÑÏõÉ Ï≤òÎ¶¨
    const handleLogout = async () => {
        try {
            await axios.post(`${BASE_URL}/users/logout`, null, {
                withCredentials: true
            });

            localStorage.removeItem('user');
            setUserInfo(null);
            toast.success('Î°úÍ∑∏ÏïÑÏõÉ ÎêòÏóàÏäµÎãàÎã§!');
            setMenuOpen(false);
            navigate('/');
        } catch (err) {
            console.error('Î°úÍ∑∏ÏïÑÏõÉ Ïã§Ìå®:', err);
            toast.error('Î°úÍ∑∏ÏïÑÏõÉ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
        }
    };

    return (
        <>
            <nav className="glass-navbar">
                <div className="navbar-container">
                    <div className="navbar-left">
                        <Link to="/" className="logo">üçΩ <span>FoodyChat</span></Link>
                    </div>

                    <div className="navbar-center desktop-menu">
                        <Link to="/chatbot" className="nav-link">ü§ñ <span>Ï±óÎ¥á</span></Link>
                        <Link to="/image-analysis" className="nav-link">üçú <span>Ïù¥ÎØ∏ÏßÄ Î∂ÑÏÑù</span></Link>
                        {userInfo && (
                            <Link to="/meal-plan" className="nav-link">ü•ó <span>ÏãùÎã®Ï°∞Ìöå</span></Link>
                        )}
                        {userInfo && (
                            <Link to="/mypage" className="nav-link">üë©‚Äçüç≥ <span>ÎßàÏù¥ÌéòÏù¥ÏßÄ</span></Link>
                        )}
                        {userInfo && userInfo.membership_level?.toLowerCase() === 'admin' && (
                            <div className="dropdown">
                                <span className="nav-link dropdown-toggle">üç± <span>Í¥ÄÎ¶¨ÏûêÌéòÏù¥ÏßÄ</span></span>
                                <div className="dropdown-menu">
                                <Link to="/users/admin" className="dropdown-item">üë• ÌöåÏõêÍ¥ÄÎ¶¨</Link>
                                <Link to="/users/admin/logs" className="dropdown-item">üìÑ ÏÇ¨Ïö©Î°úÍ∑∏Ï°∞Ìöå</Link>
                                <Link to="/users/admin/codes" className="dropdown-item">üóÇ Í≥µÌÜµÏΩîÎìúÍ¥ÄÎ¶¨</Link>
                                </div>
                            </div>
                        )}
                    </div>

                    <div className="navbar-right desktop-menu">
                        {userInfo ? (
                            <span onClick={handleLogout} className="nav-link logout-link">Î°úÍ∑∏ÏïÑÏõÉ</span>
                        ) : (
                            <>
                                <Link to="/login" className="nav-button">Î°úÍ∑∏Ïù∏</Link>
                                <Link to="/signup" className="nav-button signup">ÌöåÏõêÍ∞ÄÏûÖ</Link>
                            </>
                        )}
                    </div>

                    <div
                        className={`hamburger ${menuOpen ? 'open' : ''}`}
                        onClick={() => setMenuOpen(!menuOpen)}
                        aria-label="Menu toggle"
                    >
                        <span />
                        <span />
                        <span />
                    </div>
                </div>
            </nav>

            <div className={`mobile-menu ${menuOpen ? 'show' : ''}`}>
                <Link to="/chatbot" onClick={() => setMenuOpen(false)}>ü§ñ Ï±óÎ¥á</Link>
                <Link to="/image-analysis" onClick={() => setMenuOpen(false)}>üçú Ïù¥ÎØ∏ÏßÄ Î∂ÑÏÑù</Link>
                <Link to="/mypage" onClick={() => setMenuOpen(false)}>üë©‚Äçüç≥ ÎßàÏù¥ÌéòÏù¥ÏßÄ</Link>
                {userInfo && userInfo.membership_level?.toLowerCase() === 'admin' && (
                    <Link to="/users/admin" onClick={() => setMenuOpen(false)}>üç± Í¥ÄÎ¶¨ÏûêÌéòÏù¥ÏßÄ</Link>
                )}
                {userInfo ? (
                    <button onClick={() => { setMenuOpen(false); handleLogout(); }} className="mobile-logout-button">
                        Î°úÍ∑∏ÏïÑÏõÉ
                    </button>
                ) : (
                    <>
                        <Link to="/login" onClick={() => setMenuOpen(false)}>Î°úÍ∑∏Ïù∏</Link>
                        <Link to="/signup" onClick={() => setMenuOpen(false)}>ÌöåÏõêÍ∞ÄÏûÖ</Link>
                    </>
                )}
            </div>
        </>
    );
}
