import React, { useState } from 'react';
import axios from 'axios';
import NavBar from './NavBar';
import './css/meal-recommend.css';
import './css/main.css';

export default function MealRecommendation() {
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [mealTypes, setMealTypes] = useState([]);
  const [recommendedMeals, setRecommendedMeals] = useState([]);

  const handleCheckboxChange = (type) => {
    setMealTypes((prev) =>
      prev.includes(type) ? prev.filter((t) => t !== type) : [...prev, type]
    );
  };

  const handleRecommend = async () => {
    try {
      const response = await axios.get('http://localhost:8000/recommend', {
        params: {
          start: startDate,
          end: endDate,
          types: mealTypes.join(',')
        }
      });
      setRecommendedMeals(response.data.result); // assumes LLM returns parsed array
    } catch (error) {
      console.error('추천 실패:', error);
    }
  };

  return (
    <>
    <NavBar />
    <div className="meal-recommendation container">
      <h2 className="title">📅 맞춤 식단 추천</h2>

      <div className="filters box">
        <label className="label">기간 선택: </label>
        <input type="date" value={startDate} onChange={(e) => setStartDate(e.target.value)} className="date-input" />
        <input type="date" value={endDate} onChange={(e) => setEndDate(e.target.value)} className="date-input" />

        <div className="checkbox-group">
          <label><input type="checkbox" value="breakfast" onChange={() => handleCheckboxChange('breakfast')} /> 조식</label>
          <label><input type="checkbox" value="lunch" onChange={() => handleCheckboxChange('lunch')} /> 중식</label>
          <label><input type="checkbox" value="dinner" onChange={() => handleCheckboxChange('dinner')} /> 석식</label>
        </div>

        <button onClick={handleRecommend} className="btn btn-primary">🍱 추천 받기</button>
      </div>

      <table className="result-table">
        <thead>
          <tr>
            <th>날짜</th>
            <th>식사구분</th>
            <th>추천메뉴</th>
          </tr>
        </thead>
        <tbody>
          {recommendedMeals.map((meal, index) => (
            <tr key={index}>
              <td>{meal.date}</td>
              <td>{meal.type}</td>
              <td>{meal.menu}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
    </>
  );
}
