import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { Tooltip } from 'react-tooltip';
import 'react-tooltip/dist/react-tooltip.css';
import './css/chatbot.css';

export default function ChatBot() {
  const navigate = useNavigate();
  const [userName, setUserName] = useState('');
  const [input, setInput] = useState('');
  const [messages, setMessages] = useState([]);
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const dropdownRef = useRef();

  useEffect(() => {
    const storedName = localStorage.getItem('user_name');
    if (storedName) setUserName(storedName);

    const handleClickOutside = (e) => {
      if (dropdownRef.current && !dropdownRef.current.contains(e.target)) {
        setDropdownOpen(false);
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  const handleSend = () => {
    if (input.trim() === '') return;
    setMessages([...messages, { sender: 'user', text: input }]);
    setInput('');
  };

  const toggleTheme = () => setIsDarkMode(!isDarkMode);

  const handleLogout = () => {
    localStorage.clear();
    navigate('/login');
  };

  return (
    <div className={`chatbot-wrapper ${isDarkMode ? 'dark-mode' : 'light-mode'}`}>
      <nav className="chatbot-navbar">
        <div className="logo" onClick={() => navigate('/')}>
          <span role="img" className="logo-icon">üçΩ</span>
          <span className="logo-text">FoodyChat</span>
        </div>

        <div className="navbar-center">
          <button
            className="upload-icon-btn"
            data-tooltip-id="upload-tooltip"
            data-tooltip-content="Ïù¥ÎØ∏ÏßÄ ÏóÖÎ°úÎìú"
            onClick={() => navigate('/image-upload')}
          >
            üì§
          </button>
          <Tooltip id="upload-tooltip" place="bottom" />
        </div>

        <div className="nav-right" ref={dropdownRef}>
          <button
            className="theme-icon-btn"
            onClick={toggleTheme}
            title={isDarkMode ? 'ÎùºÏù¥Ìä∏ Î™®ÎìúÎ°ú Ï†ÑÌôò' : 'Îã§ÌÅ¨ Î™®ÎìúÎ°ú Ï†ÑÌôò'}
          >
            {isDarkMode ? 'üåô' : '‚òÄÔ∏è'}
          </button>

          {userName && (
            <div className="user-dropdown">
              <span className="user-name" onClick={() => setDropdownOpen(!dropdownOpen)}>
                üë§ {userName}
              </span>

              {dropdownOpen && (
                <div className="dropdown-menu">
                  <div className="dropdown-item">ÏÉÅÏÑ∏Ï†ïÎ≥¥</div>
                  <div className="dropdown-item" onClick={handleLogout}>Î°úÍ∑∏ÏïÑÏõÉ</div>
                </div>
              )}
            </div>
          )}
        </div>
      </nav>

      <div className="chatbot-main">
        <div className="chat-feature-panel">
          <h2>üìä Í∏∞Îä• Ìå®ÎÑê</h2>
          <p>Ïó¨Í∏∞Ïóê Îã§ÏñëÌïú Î∂ÑÏÑù ÎèÑÍµ¨Í∞Ä Îì§Ïñ¥Í∞à Ïàò ÏûàÏäµÎãàÎã§.</p>
        </div>

        <div className="chatbot-panel">
          <div className="chat-title-bar">
            <span className="chat-icon">üí¨</span>
            <h3 className="chat-title">Ï±óÎ¥á</h3>
          </div>

          <div className="chatbot-window">
            {messages.map((msg, idx) => (
              <div key={idx} className={`message ${msg.sender}`}>
                {msg.text}
              </div>
            ))}
          </div>

          <div className="chatbot-input-area">
            <input
              type="text"
              value={input}
              placeholder="Î©îÏãúÏßÄÎ•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî..."
              onChange={(e) => setInput(e.target.value)}
              onKeyDown={(e) => e.key === 'Enter' && handleSend()}
            />
            <button onClick={handleSend}>‚û§</button>
          </div>
        </div>
      </div>
    </div>
  );
}
