import React, { useEffect, useState } from 'react';
import axios from 'axios';
import NavBar from '../components/NavBar';
import './css/main.css';
import './css/admin.css';
import './css/common-code.css';

const BASE_URL = process.env.REACT_APP_BASE_URL;

export default function CommonCodePage() {
  const [codes, setCodes] = useState([]);

  const fetchCodes = async () => {
    try {
      const res = await axios.get(`${BASE_URL}/admin/common-codes`, { withCredentials: true });
      setCodes(res.data || []);
    } catch (err) {
      console.error('❌ 코드 목록 불러오기 실패:', err);
    }
  };

  useEffect(() => {
    fetchCodes();
  }, []);

  const handleRowClick = (code) => {
    console.log('🧩 선택한 코드:', code);
    // TODO: 모달 오픈 및 세부항목 조회 로직 연결
  };

  return (
    <>
      <NavBar />
      <div className="admin-wrapper">
        <div className="admin-inner">
          <h2 className="section-title">공통 코드 관리</h2>

          <div className="common-code-table-container">
            <table className="admin-table common-code-table">
              <thead>
                <tr>
                  <th>코드 ID</th>
                  <th>코드명</th>
                  <th>등록일</th>
                  <th>등록자 IP</th>
                </tr>
              </thead>
              <tbody>
                {codes.map(code => (
                  <tr key={code.code_id} onClick={() => handleRowClick(code)} className="code-row">
                    <td>{code.code_id}</td>
                    <td>{code.code_name}</td>
                    <td>{code.reg_date}</td>
                    <td>{code.reg_ip}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          {/* ✅ 모달창 */}
          {showModal && selectedCode && (
            <div className="modal-overlay" onClick={() => setShowModal(false)}>
            <div className="modal-content" onClick={(e) => e.stopPropagation()}>
                <h3>🔍 {selectedCode.code_name} 상세 항목</h3>
                <p>코드 ID: {selectedCode.code_id}</p>
                {/* 여기에 detail 목록과 등록/수정/삭제 버튼 넣을 예정 */}
                <button onClick={() => setShowModal(false)}>닫기</button>
            </div>
            </div>
          )}
        </div>
      </div>
    </>
  );
}
