import React, { useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { useNavigate } from 'react-router-dom'; // üëà ÌéòÏù¥ÏßÄ Ïù¥ÎèôÏùÑ ÏúÑÌï¥ Ï∂îÍ∞Ä
import { GoogleLogin } from '@react-oauth/google';
import NavBar from './NavBar';
import { toast } from 'react-toastify';
import './css/login.css';

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate(); // üëà ÌéòÏù¥ÏßÄ Ïù¥Îèô Ìï®Ïàò

  const handleGoogleSignup = async (credentialResponse) => {
    try {
        const res = await axios.post('http://localhost:8080/users/googleLogin', {
            token: credentialResponse.credential,
        },
        {
          headers: {
            "Content-Type": "application/json"
          },
          withCredentials: true
        }
      );

      if (res.status === 200) {
        toast.success('Î°úÍ∑∏Ïù∏ ÏÑ±Í≥µ!');
        console.log(res.data); // üëà Ïú†Ï†Ä Ï†ïÎ≥¥ ÌôïÏù∏

        // Ïú†Ï†Ä Ï†ïÎ≥¥ Ï†ÄÏû• (Ïòà: localStorage)
        localStorage.setItem('user', JSON.stringify(res.data));

        // ÌéòÏù¥ÏßÄ Ïù¥Îèô
        setTimeout(() => {
          navigate('/');
        }, 1000);
      }
    } catch (err) {
        console.error(err);
        alert("Íµ¨Í∏Ä Î°úÍ∑∏Ïù∏ Ïã§Ìå®");
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();
  
    try {
      const response = await axios.post(
        'http://localhost:8080/users/loginUser', 
        new URLSearchParams({
          email: username,
          user_password: password,
        }),
        {
          headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
          withCredentials: true,
        }
      );

      if (response.status === 200) {
        toast.success('Î°úÍ∑∏Ïù∏ ÏÑ±Í≥µ!');
        console.log(response.data); // üëà Ïú†Ï†Ä Ï†ïÎ≥¥ ÌôïÏù∏

        // Ïú†Ï†Ä Ï†ïÎ≥¥ Ï†ÄÏû• (Ïòà: localStorage)
        localStorage.setItem('user', JSON.stringify(response.data));

        // ÌéòÏù¥ÏßÄ Ïù¥Îèô
        setTimeout(() => {
          navigate('/');
        }, 1000);
      }
    } catch (err) {
      setError('Î°úÍ∑∏Ïù∏ Ïã§Ìå®: ÏïÑÏù¥Îîî ÎòêÎäî ÎπÑÎ∞ÄÎ≤àÌò∏Î•º ÌôïÏù∏ÌïòÏÑ∏Ïöî.');
      console.error(err);
    }
  };

  return (
    <>
    <NavBar />
    <div className="container">
      <div className="text-center">
          <h1>Îã§Ïãú Ïò§Ïã† Í±∏ ÌôòÏòÅÌï©ÎãàÎã§</h1>
      </div>
      <form onSubmit={handleLogin}>
          <div className="form-group">
              <input 
                  type="text"
                  className="form-control" 
                  placeholder="Ïù¥Î©îÏùº*" 
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  required 
              />
          </div>
          <div className="form-group">
              <input 
                  type="password" 
                  className="form-control" 
                  placeholder="ÎπÑÎ∞ÄÎ≤àÌò∏*" 
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required 
              />
          </div>
          {error && <div className="error-message">{error}</div>}
          <button type="submit" className="btn btn-primary">Í≥ÑÏÜç</button>
          
          <div className="find-account-links">
              <Link to="/findaccount" className="small">Ïù¥Î©îÏùº Ï∞æÍ∏∞ | ÎπÑÎ∞ÄÎ≤àÌò∏ Ï∞æÍ∏∞</Link>
          </div>
          
          <div className="divider">ÎòêÎäî</div>
          
          <GoogleLogin
            onSuccess={(credentialResponse) => {
              console.log('Íµ¨Í∏Ä Î°úÍ∑∏Ïù∏ ÏÑ±Í≥µ', credentialResponse);
              handleGoogleSignup(credentialResponse); // ‚úÖ Ïù¥ Ï§ÑÏùÑ Ï∂îÍ∞Ä
            }}
            onError={() => {
              console.log('Íµ¨Í∏Ä Î°úÍ∑∏Ïù∏ Ïã§Ìå®');
            }}
          />
      </form>
      <div className="text-center footer-links">
          <Link to="/terms" className="small">Ïù¥Ïö©ÏïΩÍ¥Ä</Link>
          <span className="divider-vertical">|</span>
          <Link to="/privacy" className="small">Í∞úÏù∏Ï†ïÎ≥¥ Î≥¥Ìò∏ Ï†ïÏ±Ö</Link>
      </div>
    </div>
    </>
  );
}

export default Login;
