import React, { useState, useEffect } from 'react';
import axios from 'axios';
import NavBar from './NavBar';
import { toast } from 'react-toastify';
import './css/meal-recommend.css';
import './css/main.css';
import { useParams } from 'react-router-dom';
import KakaoMap from './KakaoMap';

const BASE_URL = process.env.REACT_APP_BASE_URL;
export default function MealRecommendation() {
  const { foodName: paramFoodName } = useParams();
  const [foodName, setFoodName] = useState(paramFoodName || '');
  const [foodNameTranslations, setFoodNameTranslations] = useState({});
  const [location, setLocation] = useState('');
  const [recommendedStore, setRecommendedStore] = useState([]);
  const [summary, setSummary] = useState('');
  const [loading, setLoading] = useState(false);

  // ‚úÖ Î≤àÏó≠ Ï†ïÎ≥¥ Í∞ÄÏ†∏Ïò§Í∏∞
  useEffect(() => {
    axios.get(`${BASE_URL}/analyze/foods/translations`)
      .then(res => {
        setFoodNameTranslations(res.data);

        if (paramFoodName && res.data[paramFoodName.toLowerCase()]) {
          setFoodName(paramFoodName.toLowerCase());
        }
      })
      .catch(err => {
        console.error('ÏùåÏãù Î≤àÏó≠ Îç∞Ïù¥ÌÑ∞ Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:', err);
      });
  }, []);

  const translatedFood = foodNameTranslations[foodName?.toLowerCase()];

  const handleRecommend = async () => {
    if (!foodName.trim()) {
      toast.error('ÏùåÏãùÏùÑ ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî!');
      return;
    }

    if (!location.trim()) {
      toast.error('ÏúÑÏπòÎ•º ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî!(Ïòà: ÏàòÏõêÏó≠)');
      return;
    }

    try {
      setLoading(true);

      const payload = new URLSearchParams({
        foodName: foodName,
        location: location,
      });

      const response = await axios.post(`${BASE_URL}/analyze/store`, payload, {
        headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
        withCredentials: true,
      });

      if (response.status === 200) {
        toast.success('Ï∂îÏ≤ú ÏôÑÎ£å!');
      }

      const storeList = response.data.slice(0, 5);
      setRecommendedStore(storeList);
      console.error('StoreList:',storeList);

      // 2. FastAPIÎ°ú Gemini ÏöîÏïΩ ÏöîÏ≤≠
      const aiResponse = await axios.post(`${BASE_URL}/cafe-recommend`, {
        food: foodName,
        location: location,
        stores: storeList
      });

      setSummary(aiResponse.data.summary);
    } catch (error) {
      console.error('Ï∂îÏ≤ú Ïã§Ìå®:', error);
      toast.error('Ï∂îÏ≤ú ÏöîÏ≤≠ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      <NavBar />
      <div className="recommend-form-container">
        <h2 className="recommend-title">üìç ÏãùÎãπ Ï∂îÏ≤ú</h2>

        <div className="recommend-form-card">
          <div className="input-group-row">
            <div className="input-item">
              <label>ÏùåÏãù ÏÑ†ÌÉù</label>
              <select value={foodName} onChange={(e) => setFoodName(e.target.value)}>
                <option value="">-- ÏùåÏãù ÏÑ†ÌÉù --</option>
                {Object.keys(foodNameTranslations).map((key) => (
                  <option key={key} value={key}>
                    {foodNameTranslations[key]} ({key})
                  </option>
                ))}
              </select>
            </div>

            <div className="input-item">
              <label>ÏúÑÏπò</label>
              <input
                placeholder="Ïòà: ÏàòÏõêÏó≠"
                value={location}
                onChange={(e) => setLocation(e.target.value)}
                onKeyDown={(e) => {
                  if (e.key === 'Enter') {
                    e.preventDefault();
                    handleRecommend();
                  }
                }}
              />
            </div>

            <div className="input-item button-wrap">
              <button onClick={handleRecommend} disabled={loading}>
                {loading ? 'Ï∂îÏ≤úÏ§ë...' : 'Ï∂îÏ≤ú Î∞õÍ∏∞'}
              </button>
            </div>
          </div>

          {translatedFood && (
            <div className="translated-food">
              <strong>ÏÑ†ÌÉùÌïú ÏùåÏãù:</strong> {translatedFood}
            </div>
          )}

          {/* ‚úÖ AI ÏöîÏïΩ Í≤∞Í≥º */}
          {summary && (
            <div className="ai-summary-box">
              <h3>ü§ñ AI ÏöîÏïΩ Ï∂îÏ≤ú</h3>
              <p>{summary}</p>
            </div>
          )}

          {/* ‚úÖ ÏãùÎãπ Î¶¨Ïä§Ìä∏ */}
          {recommendedStore.length > 0 && (
            <>
              <KakaoMap stores={recommendedStore} />   {/* ‚úÖ ÏßÄÎèÑ ÏÇΩÏûÖ */}
              <div className="result-section">
                <h3 className="result-title">üîñ Ï∂îÏ≤ú ÏãùÎãπ Î™©Î°ù</h3>
                {recommendedStore.map((store, index) => (
                  <div key={index} className="result-item">
                    <label>ÏãùÎãπÎ™Ö</label>
                    <input type="text" readOnly value={store.name} />
                    <label>ÎßÅÌÅ¨</label>
                    <input
                      type="text"
                      readOnly
                      value={store.url}
                      onClick={() => window.open(store.url, '_blank')}
                      style={{ cursor: 'pointer' }}
                    />
                  </div>
                ))}
              </div>
            </>
          )}

          {!loading && recommendedStore.length === 0 && (
            <p className="no-result">Ï∂îÏ≤úÎêú ÏãùÎãπÏù¥ ÏóÜÏäµÎãàÎã§. Ï£ºÏÜåÎ•º ÏûÖÎ†•Ìï¥ Ï£ºÏÑ∏Ïöî.</p>
          )}
        </div>
      </div>
    </>
  );
}
