import { useEffect, useState } from "react";
import axios from "axios";
import NavBar from './NavBar';

function ChatTest() {
  const [userId, setUserId] = useState(null);
  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    axios.get("http://localhost:8080/users/user-id", { withCredentials: true })
      .then(res => setUserId(res.data))
      .catch(err => {
        console.error("‚ùå Ïú†Ï†Ä Ï†ïÎ≥¥ Î°úÎî© Ïã§Ìå®", err);
      });
  }, []);

  const handleSend = async () => {
    if (!question.trim()) return;
    if (!userId) {
      setAnswer("‚ùó Î°úÍ∑∏Ïù∏Ìïú Ïú†Ï†ÄÎßå ÏßàÎ¨∏Ìï† Ïàò ÏûàÏäµÎãàÎã§.");
      return;
    }

    setLoading(true);
    setAnswer(""); // Î°úÎî© Ï§ë Ïù¥Ï†Ñ ÏùëÎãµ Ï¥àÍ∏∞Ìôî
    try {
      const res = await axios.post("http://localhost:8000/chat/chat", {
        user_id: userId,
        question,
      });
      setAnswer(res.data.answer);
    } catch (err) {
      setAnswer(`‚ö†Ô∏è ÏÑúÎ≤Ñ Ïò§Î•ò: ${err.response?.data?.detail || err.message}`);
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
                <NavBar />
      <div style={styles.container}>
        <h1 style={styles.header}>ü§ñ Gemini Ï±óÎ¥á</h1>

        {!userId ? (
          <p style={styles.status}>üîê Î°úÍ∑∏Ïù∏ Ïú†Ï†Ä Ï†ïÎ≥¥Î•º Î∂àÎü¨Ïò§Îäî Ï§ëÏûÖÎãàÎã§...</p>
        ) : (
          <>
            <div style={styles.chatBox}>
              {/* ÏÇ¨Ïö©Ïûê ÏßàÎ¨∏ ÎßêÌíçÏÑ† */}
              {question && (
                <div style={{ ...styles.bubble, ...styles.userBubble }}>
                  {question}
                </div>
              )}

              {/* Ï±óÎ¥á ÏùëÎãµ ÎßêÌíçÏÑ† */}
              {loading ? (
                <div style={{ ...styles.bubble, ...styles.botBubble }}>
                  <span className="blinking">üí¨ ÏÉùÍ∞Å Ï§ë...</span>
                </div>
              ) : (
                answer && (
                  <div style={{ ...styles.bubble, ...styles.botBubble }}>
                    <pre style={styles.pre}>{answer}</pre>
                  </div>
                )
              )}
            </div>

            <div style={styles.inputArea}>
              <textarea
                value={question}
                onChange={(e) => setQuestion(e.target.value)}
                placeholder="ÏßàÎ¨∏ÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
                rows="4"
                style={styles.textarea}
              />
              <button onClick={handleSend} disabled={loading} style={styles.button}>
                {loading ? "Ï†ÑÏÜ° Ï§ë..." : "ÏßàÎ¨∏ Î≥¥ÎÇ¥Í∏∞"}
              </button>
            </div>
          </>
        )}
      </div>
      </>
  );
}

const styles = {
  container: {
    maxWidth: "800px",
    margin: "0 auto",
    padding: "2rem",
    fontFamily: "Segoe UI, sans-serif",
    background: "#f9f9f9",
    minHeight: "100vh",
  },
  header: {
    fontSize: "2rem",
    marginBottom: "1rem",
    textAlign: "center",
    color: "#333",
  },
  status: {
    fontStyle: "italic",
    color: "#666",
  },
  chatBox: {
    display: "flex",
    flexDirection: "column",
    gap: "1rem",
    marginBottom: "1.5rem",
    padding: "1rem",
    backgroundColor: "#fff",
    borderRadius: "12px",
    boxShadow: "0 2px 8px rgba(0,0,0,0.05)",
    minHeight: "200px",
  },
  bubble: {
    padding: "0.75rem 1rem",
    borderRadius: "16px",
    maxWidth: "100%", // ‚úÖ Ï†ÑÏ≤¥ Ïª®ÌÖåÏù¥ÎÑà Í∏∞Ï§Ä ÏµúÎåÄ
    wordBreak: "break-word", // ‚úÖ Îã®Ïñ¥ Î∂ÑÌï† ÌóàÏö©
    overflowWrap: "break-word", // ‚úÖ Ï§ÑÎ∞îÍøà
    whiteSpace: "pre-wrap", // ‚úÖ Í∞úÌñâ Ïú†ÏßÄ + ÏûêÎèô Ï§ÑÎ∞îÍøà
    lineHeight: "1.6",
  },
  userBubble: {
    backgroundColor: "#DCF8C6",
    alignSelf: "flex-end",
  },
  botBubble: {
    backgroundColor: "#ECECEC",
    alignSelf: "flex-start",
  },
  inputArea: {
    display: "flex",
    flexDirection: "column",
    gap: "0.5rem",
  },
  textarea: {
    width: "100%",
    padding: "1rem",
    fontSize: "1rem",
    borderRadius: "8px",
    border: "1px solid #ccc",
    resize: "none",
  },
  button: {
    alignSelf: "flex-end",
    backgroundColor: "#4F46E5",
    color: "#fff",
    padding: "0.75rem 1.25rem",
    border: "none",
    borderRadius: "8px",
    cursor: "pointer",
    fontSize: "1rem",
  },
  pre: {
    margin: 0,
    fontFamily: "inherit",
    wordBreak: "break-word",
    overflowWrap: "break-word",
    whiteSpace: "pre-wrap",
  },
};

export default ChatTest;
