import React, { useEffect, useState } from 'react';
import axios from 'axios';
import NavBar from '../components/NavBar';
import './css/admin.css';
import './css/main.css';
import './css/common-code.css';

const BASE_URL = process.env.REACT_APP_BASE_URL;

export default function CommonCodePage() {
  const [codes, setCodes] = useState([]);
  const [selectedCode, setSelectedCode] = useState(null);
  const [modalType, setModalType] = useState(null); // 'create' or 'edit'
  const [newCode, setNewCode] = useState({ code_id: '', code_name: '' });
  const [details, setDetails] = useState([]);

  useEffect(() => {
    fetchCodes();
  }, []);

  const fetchCodes = async () => {
    try {
      const res = await axios.get(`${BASE_URL}/users/admin/codes`, { withCredentials: true });
      const result = Array.isArray(res.data) ? res.data : res.data.codes; // ÏïàÏ†Ñ Ï≤òÎ¶¨
      setCodes(result);
    } catch (err) {
      console.error('‚ùå ÏΩîÎìú Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:', err);
      setCodes([]); // ÏïàÏ†Ñ Ï¥àÍ∏∞Ìôî
    }
  };

  const openCreateModal = () => {
    setNewCode({ code_id: '', code_name: '' });
    setDetails([]);
    setModalType('create');
  };

  const openEditModal = async (code) => {
    setSelectedCode(code);
    setNewCode({ code_id: code.code_id, code_name: code.code_name });
    try {
      const res = await axios.get(`${BASE_URL}/users/admin/codes/${code.code_id}/details`, { withCredentials: true });
      setDetails(res.data);
      setModalType('edit');
    } catch (err) {
      console.error('‚ùå ÏÑ∏Î∂Ä ÏΩîÎìú Ï°∞Ìöå Ïã§Ìå®:', err);
    }
  };

  const closeModal = () => {
    setModalType(null);
    setSelectedCode(null);
    setDetails([]);
  };

  const handleAddDetailRow = () => {
    const newDetail = {
      detail_code: '',
      detail_name: '',
      reg_id: 'Í¥ÄÎ¶¨Ïûê',
      reg_date: new Date().toLocaleString(),
    };
    setDetails([...details, newDetail]);
  };

  const handleDeleteCode = async (codeId) => {
    if (!window.confirm(`ÏΩîÎìú ${codeId}ÏùÑ(Î•º) ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?`)) return;
  
    try {
      await axios.delete(`${BASE_URL}/users/admin/codes/delete/${codeId}`, {
        withCredentials: true,
      });
      fetchCodes(); // ÏÇ≠Ï†ú ÌõÑ Î™©Î°ù ÏÉàÎ°úÍ≥†Ïπ®
    } catch (err) {
      console.error('‚ùå ÏΩîÎìú ÏÇ≠Ï†ú Ïã§Ìå®:', err);
    }
  };

  const handleDeleteDetailRow = async (index) => {
    const detail = details[index];

    // ÏÑúÎ≤ÑÏóê Ï†ÄÏû•Îêú Ìï≠Î™©Ïùº Í≤ΩÏö∞Îßå DELETE ÏöîÏ≤≠
    if (detail.detail_code && modalType === 'edit') {
      try {
        await axios.delete(
          `${BASE_URL}/users/admin/codes/delete/${newCode.code_id}/details/${detail.detail_code}`,
          { withCredentials: true }
        );
      } catch (err) {
        console.error('‚ùå ÏÑ∏Î∂Ä Ìï≠Î™© ÏÇ≠Ï†ú Ïã§Ìå®:', err);
        return;
      }
    }

    // ÌîÑÎ°†Ìä∏ÏóîÎìúÏóêÏÑú Î™©Î°ù Ï†úÍ±∞
    const updatedDetails = [...details];
    updatedDetails.splice(index, 1);
    setDetails(updatedDetails);
  };

  return (
    <>
      <NavBar />
      <div className="admin-wrapper">
        <div className="admin-inner">
          <h2 className="section-title">Í≥µÌÜµ ÏΩîÎìú Í¥ÄÎ¶¨</h2>
          <div className="scroll-table-container">
            <div className="table-header">
              <button className="btn ghost-icon-button" onClick={openCreateModal}>Ôºã ÏΩîÎìú Îì±Î°ù</button>
            </div>
            <table className="admin-table">
              <colgroup>
                <col style={{ width: '14%' }} />
                <col style={{ width: '40%' }} />
                <col style={{ width: '18%' }} />
                <col style={{ width: '18%' }} />
                <col style={{ width: '10%' }} />
              </colgroup>
              <thead>
                <tr>
                  <th>ÏΩîÎìúID</th>
                  <th>ÏΩîÎìúÎ™Ö</th>
                  <th>Îì±Î°ùÏûê</th>
                  <th>Îì±Î°ùÏùº</th>
                  <th>ÏÇ≠Ï†ú</th>
                </tr>
              </thead>
              <tbody>
                {Array.isArray(codes) && codes.map(code => (
                  <tr key={code.code_id} onClick={() => openEditModal(code)} style={{ cursor: 'pointer' }}>
                    <td>{code.code_id}</td>
                    <td className="code-name-column">{code.code_name}</td>
                    <td>{code.reg_id}</td>
                    <td>{code.reg_date}</td>
                    <td>
                      <button
                        className="delete-btn"
                        onClick={(e) => {
                          e.stopPropagation(); // ÏàòÏ†ï Î™®Îã¨ Ïó¥Î¶¨ÏßÄ ÏïäÎèÑÎ°ù
                          handleDeleteCode(code.code_id);
                        }}
                      >
                        ÏÇ≠Ï†ú
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          {modalType && (
            <div className="modal-overlay" onClick={closeModal}>
              <div className="modal-content" onClick={(e) => e.stopPropagation()}>
                <div className="modal-header">
                  <h3>{modalType === 'edit' ? '‚úèÔ∏è ÏΩîÎìú ÏàòÏ†ï' : 'üìå ÏΩîÎìú Îì±Î°ù'}</h3>
                  <button onClick={closeModal} className="close-button">√ó</button>
                </div>

                <div className="modal-body compact-body">
                  <div className="input-group-horizontal">
                    <div className="input-subgroup code-id-group">
                      <label>ÏΩîÎìú ID</label>
                      <input
                        type="text"
                        className="form-input-sm"
                        value={newCode.code_id}
                        disabled={modalType === 'edit'}
                        onChange={(e) => setNewCode({ ...newCode, code_id: e.target.value })}
                      />
                    </div>
                    <div className="input-subgroup code-name-group">
                      <label>ÏΩîÎìúÎ™Ö</label>
                      <input
                        type="text"
                        className="form-input-sm"
                        value={newCode.code_name}
                        onChange={(e) => setNewCode({ ...newCode, code_name: e.target.value })}
                      />
                    </div>
                  </div>
                </div>

                <div className="detail-table-wrapper">
                  <table className="admin-table compact-table">
                    <thead>
                      <tr>
                        <th>ÏΩîÎìú</th>
                        <th>ÏΩîÎìúÎ™Ö</th>
                        <th>Îì±Î°ùÏûê</th>
                        <th>Îì±Î°ùÏùº</th>
                        <th>ÏÇ≠Ï†ú</th>
                      </tr>
                    </thead>
                    <tbody>
                      {details.map((detail, idx) => (
                        <tr key={idx}>
                          <td><input className="table-input" value={detail.detail_code} onChange={(e) => {
                            const newDetails = [...details];
                            newDetails[idx].detail_code = e.target.value;
                            setDetails(newDetails);
                          }} /></td>
                          <td><input className="table-input" value={detail.detail_name} onChange={(e) => {
                            const newDetails = [...details];
                            newDetails[idx].detail_name = e.target.value;
                            setDetails(newDetails);
                          }} /></td>
                          <td>{detail.reg_id}</td>
                          <td>{detail.reg_date}</td>
                          <td>
                            <button className="delete-btn" onClick={() => handleDeleteDetailRow(idx)}>ÏÇ≠Ï†ú</button>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>

                <div className="modal-footer">
                  <button onClick={handleAddDetailRow} className="btn secondary">Ìñâ Ï∂îÍ∞Ä</button>
                  <button
                    className="btn primary"
                    onClick={async () => {
                      try {
                        const payload = {
                          code: newCode,
                          details: details
                        };

                        await axios.post(`${BASE_URL}/users/admin/codes/save`, payload, {
                          withCredentials: true,
                        });

                        closeModal();
                        setNewCode({ code_id: '', code_name: '' });
                        fetchCodes();
                      } catch (err) {
                        console.error('‚ùå Ï†ÄÏû• Ïã§Ìå®:', err);
                      }
                    }}
                  >
                    Ï†ÄÏû•
                  </button>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </>
  );
}
