import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './css/main.css';
import './css/snap.css';
import img1 from './img/img11.png';
import img2 from './img/img22.png';

export default function MainPage() {
  const navigate = useNavigate();
  const [userInfo, setUserInfo] = useState(null);

  useEffect(() => {
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      setUserInfo(JSON.parse(storedUser));
    }
  }, []);

  useEffect(() => {
    document.body.style.overflow = 'hidden';
    return () => {
      document.body.style.overflow = 'auto';
    };
  }, []);

  const handleLogout = async () => {
    try {
      await axios.post('http://localhost:8080/users/logout',
        new URLSearchParams({
          user_id: userInfo.user_id,
        }),
        {
          headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
          withCredentials: true,
        }
      );
      localStorage.removeItem('user');
      setUserInfo(null);
      navigate('/login');
    } catch (err) {
      console.error('로그아웃 실패:', err);
      alert('로그아웃 중 오류가 발생했습니다.');
    }
  };

  return (
    <>
      {/* ✅ 네비게이션 바 */}
      <nav className="pinterest-navbar">
        <div className="nav-left">
          <span role="img" aria-label="logo" className="logo-icon">🍽</span>
          <span className="logo-text">FoodyChat</span>
        </div>

        <div className="nav-center-buttons">
          <button className="go-chat-btn" onClick={() => navigate('/chatbot')}>
            💬 챗봇으로 가기
          </button>
          <button className="go-upload-btn" onClick={() => navigate('/upload')}>
            📤 이미지 업로드
          </button>
        </div>

        <div className="nav-right">
          {userInfo ? (
            <>
              <span className="welcome-text">{userInfo.nickname || userInfo.email}님</span>
              <button className="logout-btn" onClick={handleLogout}>로그아웃</button>
            </>
          ) : (
            <>
              <button className="login-btn" onClick={() => navigate('/login')}>로그인</button>
              <button className="signup-btn" onClick={() => navigate('/signup')}>가입하기</button>
            </>
          )}
        </div>
      </nav>

      {/* 스크롤 섹션 */}
      <div className="scroll-container">
        <section className="scroll-item image-section bg-upload">
          <img src={img1} alt="요리 업로드 안내" className="snap-img" />
          <div className="overlay-box">
            <h3>📸 찍어서 업로드 하세요</h3>
            <p>당신의 요리를 분석해드려요.</p>
          </div>
        </section>

        <section className="scroll-item image-section bg-nutrition">
          <img src={img2} alt="영양 체크" className="snap-img smaller-height" />
          <div className="overlay-box">
            <h3>🥗 Nutrition Check</h3>
            <p>영양 성분을 손쉽게 확인하세요.</p>
          </div>
        </section>
      </div>
    </>
  );
}
