import React, { useEffect, useState } from 'react';
import axios from 'axios';
import NavBar from '../components/NavBar';
import './css/admin.css';
import './css/main.css';
import './css/common-code.css';

const BASE_URL = process.env.REACT_APP_BASE_URL;

export default function CommonCodePage() {
  const [codes, setCodes] = useState([]);
  const [selectedCode, setSelectedCode] = useState(null);
  const [detailModalOpen, setDetailModalOpen] = useState(false);
  const [createModalOpen, setCreateModalOpen] = useState(false);
  const [newCode, setNewCode] = useState({ code_id: '', code_name: '' });
  const [details, setDetails] = useState([]);

  useEffect(() => {
    fetchCodes();
  }, []);

  const fetchCodes = async () => {
    try {
      const res = await axios.get(`${BASE_URL}/users/admin/codes`, { withCredentials: true });
      setCodes(res.data);
    } catch (err) {
      console.error('‚ùå ÏΩîÎìú Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:', err);
    }
  };

  const handleRowClick = async (code) => {
    setSelectedCode(code);
    try {
      const res = await axios.get(`${BASE_URL}/codes/${code.code_id}/details`, { withCredentials: true });
      setDetails(res.data);
      setDetailModalOpen(true);
    } catch (err) {
      console.error('‚ùå ÏÑ∏Î∂Ä ÏΩîÎìú Ï°∞Ìöå Ïã§Ìå®:', err);
    }
  };

  const closeModal = () => {
    setDetailModalOpen(false);
    setSelectedCode(null);
    setDetails([]);
  };

  return (
    <>
      <NavBar />
      <div className="admin-wrapper">
        <div className="admin-inner">
          <h2 className="section-title">Í≥µÌÜµ ÏΩîÎìú Í¥ÄÎ¶¨</h2>
          <div className="scroll-table-container">
            <div className="table-header">
                <button className="btn ghost-icon-button" onClick={() => setCreateModalOpen(true)}>Ôºã ÏΩîÎìú Îì±Î°ù</button>
            </div>
            <table className="admin-table">
              <colgroup>
                <col style={{ width: '15%' }} />
                <col style={{ width: '45%' }} />
                <col style={{ width: '20%' }} />
                <col style={{ width: '20%' }} />
              </colgroup>
              <thead>
                <tr>
                  <th>ÏΩîÎìúID</th>
                  <th>ÏΩîÎìúÎ™Ö</th>
                  <th>Îì±Î°ùÏûê</th>
                  <th>Îì±Î°ùÏùº</th>
                </tr>
              </thead>
              <tbody>
                {codes.map((code) => (
                  <tr key={code.code_id} onClick={() => handleRowClick(code)} style={{ cursor: 'pointer' }}>
                    <td>{code.code_id}</td>
                    <td className="code-name-column">{code.code_name}</td>
                    <td>{code.reg_id}</td>
                    <td>{code.reg_date}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
          {createModalOpen && (
            <div className="modal-overlay" onClick={() => setCreateModalOpen(false)}>
                <div className="modal-content" onClick={(e) => e.stopPropagation()}>
                <div className="modal-header">
                    <h3>üìå ÏΩîÎìú Îì±Î°ù</h3>
                    <button onClick={() => setCreateModalOpen(false)} className="close-button">X</button>
                </div>
                <div className="modal-body">
                    <input
                        className="form-input"
                        type="text"
                        placeholder="ÏΩîÎìú ID"
                        value={newCode.code_id}
                        onChange={(e) => setNewCode({ ...newCode, code_id: e.target.value })}
                    />
                    <input
                        className="form-input"
                        type="text"
                        placeholder="ÏΩîÎìúÎ™Ö"
                        value={newCode.code_name}
                        onChange={(e) => setNewCode({ ...newCode, code_name: e.target.value })}
                    />
                </div>
                <div className="modal-footer">
                    <button
                        className="btn primary"
                        onClick={async () => {
                            try {
                                await axios.post(`${BASE_URL}/users/admin/codes`, newCode, { withCredentials: true });
                                setCreateModalOpen(false);
                                setNewCode({ code_id: '', code_name: '' });
                                fetchCodes();
                            } catch (err) {
                                console.error('‚ùå ÏΩîÎìú Îì±Î°ù Ïã§Ìå®:', err);
                            }
                        }}
                    >
                    Ï†ÄÏû•
                    </button>
                </div>
                </div>
            </div>
          )}

          {detailModalOpen && (
            <div className="modal-overlay" onClick={closeModal}>
              <div className="modal-content" onClick={(e) => e.stopPropagation()}>
                <div className="modal-header">
                  <h3>ÏÑ∏Î∂Ä ÏΩîÎìú Î™©Î°ù - {selectedCode.code_name}</h3>
                  <button onClick={closeModal} className="close-button">X</button>
                </div>
                <table className="admin-table">
                  <thead>
                    <tr>
                      <th>Detail Code</th>
                      <th>Detail Name</th>
                      <th>Îì±Î°ùÏûê</th>
                      <th>Îì±Î°ùÏùº</th>
                    </tr>
                  </thead>
                  <tbody>
                    {details.map((detail, idx) => (
                      <tr key={idx}>
                        <td>{detail.detail_code}</td>
                        <td>{detail.detail_name}</td>
                        <td>{detail.reg_id}</td>
                        <td>{detail.reg_date}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          )}
        </div>
      </div>
    </>
  );
}
