import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import NavBar from './NavBar';
import { useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';
import { Pie } from 'react-chartjs-2'; // Pie Ï∞®Ìä∏ Í∞ÄÏ†∏Ïò§Í∏∞
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js'; // Chart.js ÏÑ§Ï†ï
import './css/main.css';
import './css/image-analysis.css';

// Chart.jsÏóêÏÑú ÌïÑÏöîÌïú ÏöîÏÜå Îì±Î°ù
ChartJS.register(ArcElement, Tooltip, Legend);

export default function Imageanalysis() {
  const [selectedImage, setSelectedImage] = useState(null);
  const [previewUrl, setPreviewUrl] = useState(null);
  const [analysisResult, setAnalysisResult] = useState(null);
  const imageInputRef = useRef();
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleImageChange = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    setSelectedImage(file);
    setPreviewUrl(URL.createObjectURL(file));
    setAnalysisResult(null);
    setLoading(true);

    try {
      const formData = new FormData();
      formData.append('file', file);

      const response = await axios.post('http://localhost:8080/analyze/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
        withCredentials: true,
      });

      console.log("üçΩ Í≤∞Í≥º:", response.data);
      setAnalysisResult(response.data);
    } catch (error) {
      toast.error('Î∂ÑÏÑù Ïã§Ìå®');
      console.error('‚ùå ÏóêÎü¨:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleRecommendation = () => {
    if (analysisResult?.predictedClass) {
      navigate(`/cafe-recommend/${analysisResult.predictedClass}`);
    } else {
      if (window.confirm("ÏòàÏ∏°Îêú ÏùåÏãùÏù¥ ÏóÜÏäµÎãàÎã§. Ï†ÑÏ≤¥ ÏãùÎãπ Î™©Î°ùÏùÑ Î≥¥ÏãúÍ≤†ÏäµÎãàÍπå?")) {
        navigate('/cafe-recommend/all');
      } else {
        toast.info("Î∂ÑÏÑù ÌõÑ Ï∂îÏ≤úÏùÑ Ïù¥Ïö©Ìï¥Î≥¥ÏÑ∏Ïöî.");
      }
    }
  };

  const chartData = {
    labels: ['ÌÉÑÏàòÌôîÎ¨º', 'Îã®Î∞±Ïßà', 'ÏßÄÎ∞©'],
    datasets: [
      {
        label: 'ÏòÅÏñëÏÜå ÎπÑÏú®',
        data: [24, 10, 18], // Ïó¨Í∏∞ÏÑú Í∞Å Í∞íÏù¥ Ï†ïÌôïÌûà Ïà´Ïûê Î∞∞Ïó¥Î°ú ÎêòÏñ¥Ïïº Ìï©ÎãàÎã§.
        backgroundColor: ['#ff6a5c', '#ffb86c', '#b5e7a0'],
        borderColor: '#ffffff',
        borderWidth: 2,
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
      },
    },
  };

  // `analysisResult`Í∞Ä Î∞îÎÄî ÎïåÎßàÎã§ Ï∞®Ìä∏ Îç∞Ïù¥ÌÑ∞ ÏóÖÎç∞Ïù¥Ìä∏
  useEffect(() => {
    if (analysisResult) {
      // Ï∞®Ìä∏ Îç∞Ïù¥ÌÑ∞ ÏóÖÎç∞Ïù¥Ìä∏
      setChartData({
        labels: ['ÌÉÑÏàòÌôîÎ¨º', 'Îã®Î∞±Ïßà', 'ÏßÄÎ∞©'],
        datasets: [
          {
            label: 'ÏòÅÏñëÏÜå ÎπÑÏú®',
            data: [
              analysisResult.nut_carb,
              analysisResult.nut_pro,
              analysisResult.nut_fat,
            ],
            backgroundColor: ['#ff6a5c', '#ffb86c', '#b5e7a0'],
            borderColor: '#ffffff',
            borderWidth: 2,
          },
        ],
      });
    }
  }, [analysisResult]); // analysisResultÍ∞Ä Î≥ÄÍ≤ΩÎê† ÎïåÎßàÎã§ Ïã§Ìñâ

  // Îç∞Ïù¥ÌÑ∞Î•º ÌôïÏù∏ÌïòÍ∏∞ ÏúÑÌïú console.log Ï∂îÍ∞Ä
  useEffect(() => {
    console.log("Chart Data:", chartData);
    console.log("Chart Options:", chartOptions);
  }, [chartData, chartOptions]);

  return (
    <>
      <NavBar />
      <div className="analysis-wrapper">
        <div className="analysis-inner">
          <h1 className="analysis-title">üçïÏù¥ÎØ∏ÏßÄ Î∂ÑÏÑùü•§</h1>

          {/* Ïù¥ÎØ∏ÏßÄ ÏóÖÎ°úÎìú ÏòÅÏó≠ */}
          <div
            className="upload-area"
            onClick={() => imageInputRef.current.click()}
            onDragOver={(e) => e.preventDefault()}
            onDrop={(e) => {
              e.preventDefault();
              const file = e.dataTransfer.files[0];
              if (file) {
                imageInputRef.current.files = e.dataTransfer.files;
                handleImageChange({ target: { files: e.dataTransfer.files } });
              }
            }}
          >
            <p>Ïù¥ÎØ∏ÏßÄÎ•º ÌÅ¥Î¶≠ÌïòÍ±∞ÎÇò ÎìúÎûòÍ∑∏ Ïï§ ÎìúÎ°≠ÌïòÏÑ∏Ïöî</p>
            <input
              type="file"
              accept="image/*"
              ref={imageInputRef}
              onChange={handleImageChange}
              style={{ display: 'none' }}
            />
          </div>

          {/* üñºÔ∏è Ïù¥ÎØ∏ÏßÄ ÎØ∏Î¶¨Î≥¥Í∏∞ */}
          {previewUrl && (
            <div className="image-preview">
              <img src={previewUrl} alt="ÏóÖÎ°úÎìúÎêú ÏùåÏãù" />
            </div>
          )}

          {/* Î∂ÑÏÑù Ï§ë Î©îÏãúÏßÄ */}
          {loading && <p className="loading-message">üîç Î∂ÑÏÑù Ï§ëÏûÖÎãàÎã§...</p>}

          {analysisResult && analysisResult.predictedClass && (
            <>
              <div className="result-card">
                <div className="predicted-class">
                  üçΩÔ∏è ÏòàÏ∏° Í≤∞Í≥º: <strong>{analysisResult.predictedClass}</strong>
                </div>
                <div className="confidence-score">
                  Ïã†Î¢∞ÎèÑ: {(analysisResult.confidence * 100).toFixed(2)}%
                </div>
              </div>

              {/* 2Ïó¥ Î†àÏù¥ÏïÑÏõÉ - ÏòÅÏñë Ï†ïÎ≥¥ ÏôºÏ™Ω, Ï∞®Ìä∏ Ïò§Î•∏Ï™Ω */}
              <div className="nutrition-and-chart">
                <div className="nutrition-card">
                  <h3>ü•ó ÏòÅÏñë Ï†ïÎ≥¥</h3>
                  <ul className="nutrition-list">
                    <li><strong>ÏπºÎ°úÎ¶¨:</strong> {analysisResult.calories} kcal</li>
                    <li><strong>ÌÉÑÏàòÌôîÎ¨º:</strong> {analysisResult.nut_carb} g</li>
                    <li><strong>Îã®Î∞±Ïßà:</strong> {analysisResult.nut_pro} g</li>
                    <li><strong>ÏßÄÎ∞©:</strong> {analysisResult.nut_fat} g</li>
                  </ul>
                </div>

                <div className="chart-container">
                  {/* Pie Ï∞®Ìä∏ Î†åÎçîÎßÅ */}
                  <Pie data={chartData} options={chartOptions} />
                </div>
              </div>
            </>
          )}

          {/* Ï∂îÏ≤ú Î≤ÑÌäº */}
          <div className="button-group">
            <button
              onClick={handleRecommendation}
              className="button secondary"
            >
              ÏãùÎãπ Ï∂îÏ≤ú
            </button>
            <button
              onClick={() => navigate('/meal-recommend')}
              className="button secondary"
            >
              ÏãùÎã® Ï∂îÏ≤ú
            </button>
          </div>
        </div>
      </div>
    </>
  );
}
