import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './css/common-code.css';

const BASE_URL = process.env.REACT_APP_BASE_URL;

export default function CommonCodePage() {
  const [codes, setCodes] = useState([]);
  const [selectedCode, setSelectedCode] = useState(null);
  const [showModal, setShowModal] = useState(false);

  const fetchCodes = async () => {
    try {
      const res = await axios.get(`${BASE_URL}/users/admin/codes`);
      setCodes(res.data || []);
    } catch (error) {
      console.error('‚ùå ÏΩîÎìú Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:', error);
    }
  };

  useEffect(() => {
    fetchCodes();
  }, []);

  const handleRowClick = (code) => {
    setSelectedCode(code);
    setShowModal(true);
  };

  return (
    <div className="common-code-wrapper">
      <h2>Í≥µÌÜµ ÏΩîÎìú Î™©Î°ù</h2>
      <div className="common-code-table-container">
        <table className="common-code-table">
            <thead>
            <tr>
                <th>ÏΩîÎìú ID</th>
                <th>ÏΩîÎìú Ïù¥Î¶Ñ</th>
                <th>Îì±Î°ùÏùº</th>
            </tr>
            </thead>
            <tbody>
            {codes.map((code) => (
                <tr key={code.code_id} onClick={() => handleRowClick(code)}>
                <td>{code.code_id}</td>
                <td>{code.code_name}</td>
                <td>{code.reg_date}</td>
                </tr>
            ))}
            </tbody>
        </table>
      </div>

      {/* ‚úÖ Î™®Îã¨Ï∞Ω */}
      {showModal && selectedCode && (
        <div className="modal-overlay" onClick={() => setShowModal(false)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <h3>üîç {selectedCode.code_name} ÏÉÅÏÑ∏ Ìï≠Î™©</h3>
            <p>ÏΩîÎìú ID: {selectedCode.code_id}</p>
            {/* Ïó¨Í∏∞Ïóê detail Î™©Î°ùÍ≥º Îì±Î°ù/ÏàòÏ†ï/ÏÇ≠Ï†ú Î≤ÑÌäº ÎÑ£ÏùÑ ÏòàÏ†ï */}
            <button onClick={() => setShowModal(false)}>Îã´Í∏∞</button>
          </div>
        </div>
      )}
    </div>
  );
}
