// src/components/Carousel.jsx

import React, { useState } from 'react';
import carouselData from './CarouselData';
import Slider from 'react-slick';
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css";
import './css/carousel.css'; // 추가 커스텀 스타일

export default function Carousel() {
  const [current, setCurrent] = useState(0);
  const currentSlide = carouselData[current];
  const length = carouselData.length;

  const nextSlide = () => {
    setCurrent((prev) => (prev + 1) % length);
  };

  const prevSlide = () => {
    setCurrent((prev) => (prev - 1 + length) % length);
  };

  const settings = {
    dots: true, // 점 네비게이션
    infinite: true, // 무한 루프
    speed: 500, // 전환 속도 (ms)
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true, // ✅ 자동 슬라이드
    autoplaySpeed: 3000, // 3초마다 넘김
    arrows: true, // ✅ 화살표 표시
  };

  return (
    <div className="carousel-container">
      <Slider {...settings}>
        <div className="carousel-slide">
          <img src={currentSlide.image} alt={`slide-${current}`} />
          <div
            className="carousel-caption"
            style={currentSlide.captionStyle}
          >
            <h3>{currentSlide.title}</h3>
            <p>{currentSlide.sub}</p>
          </div>
        </div>
        <div
          className="carousel-description"
          style={currentSlide.descriptionStyle} // ✅ 글자 색상만 적용
        >
          {currentSlide.description}
        </div>
      </Slider>
    </div>
  );
}
