import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import NavBar from './NavBar';
import './css/signup2.css';

export default function Signup2() {
    const BASE_URL = process.env.REACT_APP_BASE_URL;
    const navigate = useNavigate();

    const [formData, setFormData] = useState({
        user_id: null,
        gender: '',
        age: '',
        height: '',
        user_weight: '',
        user_address: '',
        health_goal: ''
    });

    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');
    const [goalOptions, setGoalOptions] = useState([]); // üí° Í±¥Í∞ï Î™©Ìëú ÏΩîÎìú Î™©Î°ù

    useEffect(() => {
        const storedUserId = localStorage.getItem('user_id');
        if (storedUserId && !isNaN(storedUserId)) {
            setFormData(prev => ({
                ...prev,
                user_id: parseInt(storedUserId, 10)
            }));
        } else {
            setError('Ïú†Ìö®ÌïòÏßÄ ÏïäÏùÄ ÏÇ¨Ïö©Ïûê IDÏûÖÎãàÎã§. Îã§Ïãú Î°úÍ∑∏Ïù∏Ìï¥Ï£ºÏÑ∏Ïöî.');
        }

        const fetchGoals = async () => {
          try {
            const res = await axios.get(`${BASE_URL}/codes/CM003/details`, {
              withCredentials: true,
            });
            console.log('‚úÖ ÏÑúÎ≤Ñ ÏùëÎãµ:', res.data);
            // ‚úÖ res.dataÎäî Ïù¥ÎØ∏ Î∞∞Ïó¥Ïù¥ÎØÄÎ°ú Î∞îÎ°ú ÏÇ¨Ïö©
            if (Array.isArray(res.data)) {
              setGoalOptions(res.data);
            } else {
              console.warn('ÏÑúÎ≤Ñ ÏùëÎãµÏù¥ Î∞∞Ïó¥Ïù¥ ÏïÑÎãôÎãàÎã§:', res.data);
              setGoalOptions([]); // fallback
            }
          } catch (err) {
            console.error('‚ùå Í±¥Í∞ïÎ™©Ìëú ÏΩîÎìú Ï°∞Ìöå Ïã§Ìå®:', err);
            setGoalOptions([]); // fallback on error
          }
        };
      
        fetchGoals();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

    const handleAddressSearch = () => {
        new window.daum.Postcode({
            oncomplete: function (data) {
                const address = data.address;
                setFormData(prev => ({ ...prev, user_address: address }));

                // ‚úÖ Ïú†Ìö®ÏÑ± ÏàòÎèô Ï¥àÍ∏∞Ìôî
                const addressInput = document.querySelector('input[name="user_address"]');
                if (addressInput) {
                    addressInput.setCustomValidity('');
                }
            }
        }).open();
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        setSuccess('');

        if (!formData.user_id) {
            setError('ÌöåÏõê Ï†ïÎ≥¥Í∞Ä Ïú†Ìö®ÌïòÏßÄ ÏïäÏäµÎãàÎã§. Îã§Ïãú Î°úÍ∑∏Ïù∏Ìï¥Ï£ºÏÑ∏Ïöî.');
            return;
        }

        try {
            const response = await axios.post(`${BASE_URL}/users/details`, formData);
            if (response.status === 200) {
                setSuccess('Í±¥Í∞ï Ï†ïÎ≥¥Í∞Ä ÏÑ±Í≥µÏ†ÅÏúºÎ°ú Ï†ÄÏû•ÎêòÏóàÏäµÎãàÎã§!');
                setTimeout(() => {
                    navigate('/');
                }, 1000);
            }
        } catch (error) {
            console.error("Ï†ÄÏû• Ï§ë Ïò§Î•ò Î∞úÏÉù:", error);
            setError('Ï†ÄÏû• Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
        }
    };

    return (
      <>
      <NavBar />
      <div className="container">
        <div className="text-center">
          <h1>Ï∂îÍ∞Ä Í±¥Í∞ï Ï†ïÎ≥¥ ÏûÖÎ†•</h1>
        </div>

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <select
              name="gender"
              value={formData.gender}
              onChange={handleChange}
              className="form-control"
              required
              onInvalid={(e) => e.target.setCustomValidity('ÏÑ±Î≥ÑÏùÑ ÏÑ†ÌÉùÌïòÏÑ∏Ïöî')}
              onInput={(e) => e.target.setCustomValidity('')}
            >
              <option value="">ÏÑ±Î≥Ñ ÏÑ†ÌÉù*</option>
              <option value="1">ÎÇ®ÏÑ±</option>
              <option value="2">Ïó¨ÏÑ±</option>
            </select>
          </div>

          <div className="form-group">
            <input
              type="number"
              name="age"
              placeholder="ÎÇòÏù¥*"
              value={formData.age}
              onChange={handleChange}
              className="form-control"
              required
              onInvalid={(e) => e.target.setCustomValidity('ÎÇòÏù¥Î•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî')}
              onInput={(e) => e.target.setCustomValidity('')}
            />
          </div>

          <div className="form-group">
            <input
              type="number"
              name="height"
              placeholder="ÌÇ§ (cm)*"
              value={formData.height}
              onChange={handleChange}
              className="form-control"
              required
              onInvalid={(e) => e.target.setCustomValidity('ÌÇ§Î•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî')}
              onInput={(e) => e.target.setCustomValidity('')}
            />
          </div>

          <div className="form-group">
            <input
              type="number"
              name="user_weight"
              placeholder="Î™∏Î¨¥Í≤å (kg)*"
              value={formData.user_weight}
              onChange={handleChange}
              className="form-control"
              required
              onInvalid={(e) => e.target.setCustomValidity('Î™∏Î¨¥Í≤åÎ•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî')}
              onInput={(e) => e.target.setCustomValidity('')}
            />
          </div>

          <div className="form-group">
            <div className="input-group">
              <input
                type="text"
                name="user_address"
                placeholder="Ï£ºÏÜå*"
                value={formData.user_address}
                onChange={handleChange}
                className="form-control"
                required
                onInvalid={(e) => e.target.setCustomValidity('Ï£ºÏÜåÎ•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî')}
                onInput={(e) => e.target.setCustomValidity('')}
              />
              <div className="input-group-append">
                <button
                    type="button"
                    className="btn btn-outline-secondary align-middle"
                    onClick={handleAddressSearch}
                >
                    Ï£ºÏÜå Í≤ÄÏÉâ
                </button>
              </div>
            </div>
          </div>

          <div className="form-group">
            <select
              name="health_goal"
              value={formData.health_goal}
              onChange={handleChange}
              className="form-control"
              required
              onInvalid={(e) => e.target.setCustomValidity('Í±¥Í∞ïÎ™©ÌëúÎ•º ÏÑ†ÌÉùÌïòÏÑ∏Ïöî')}
              onInput={(e) => e.target.setCustomValidity('')}
            >
              <option value="">Í±¥Í∞ï Î™©Ìëú*</option>
              {goalOptions.map((goal) => (
              <option key={goal.code_id} value={goal.code_id}>
                {goal.code_name}
              </option>
            ))}
            </select>
          </div>

          {error && <div className="error-message">{error}</div>}
          {success && <div className="success-message">{success}</div>}

          <button type="submit" className="btn btn-primary w-100">Ï†úÏ∂úÌïòÍ∏∞</button>
        </form>
      </div>
    </>
    );
}
