import React, { useState, useRef } from 'react';
import axios from 'axios';
import NavBar from './NavBar';
import { useNavigate } from 'react-router-dom';
import { Pie } from 'react-chartjs-2'; // Chart.js 라이브러리 사용
import { toast } from 'react-toastify';
import './css/main.css';
import './css/image-analysis.css';

export default function Imageanalysis() {
  const [selectedImage, setSelectedImage] = useState(null);
  const [previewUrl, setPreviewUrl] = useState(null);
  const [analysisResult, setAnalysisResult] = useState(null);
  const imageInputRef = useRef();
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleImageChange = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    setSelectedImage(file);
    setPreviewUrl(URL.createObjectURL(file));
    setAnalysisResult(null);
    setLoading(true);

    try {
      const formData = new FormData();
      formData.append('file', file);

      const response = await axios.post('http://localhost:8080/analyze/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
        withCredentials: true,
      });

      console.log("🍽 결과:", response.data);
      setAnalysisResult(response.data);
    } catch (error) {
      toast.error('분석 실패');
      console.error('❌ 에러:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleRecommendation = () => {
    if (analysisResult?.predictedClass) {
      navigate(`/cafe-recommend/${analysisResult.predictedClass}`);
    } else {
      if (window.confirm("예측된 음식이 없습니다. 전체 식당 목록을 보시겠습니까?")) {
        navigate('/cafe-recommend/all');
      } else {
        toast.info("분석 후 추천을 이용해보세요.");
      }
    }
  };

  // 차트 데이터 생성 (칼로리, 탄수화물, 단백질, 지방)
  const chartData = {
    labels: ['칼로리', '탄수화물', '단백질', '지방'],
    datasets: [
      {
        label: '영양소 비율',
        data: [
          analysisResult ? analysisResult.calories : 0,
          analysisResult ? analysisResult.nut_carb : 0,
          analysisResult ? analysisResult.nut_pro : 0,
          analysisResult ? analysisResult.nut_fat : 0,
        ],
        backgroundColor: ['#ff9a8b', '#ff6a5c', '#ffb86c', '#b5e7a0'],
        borderColor: '#ffffff',
        borderWidth: 2,
      },
    ],
  };

  return (
    <>
      <NavBar />
      <div className="analysis-wrapper">
        <div className="analysis-inner">
          <h1 className="analysis-title">🍕이미지 분석🥤</h1>

          {/* 이미지 업로드 영역 */}
          <div
            className="upload-area"
            onClick={() => imageInputRef.current.click()}
            onDragOver={(e) => e.preventDefault()}
            onDrop={(e) => {
              e.preventDefault();
              const file = e.dataTransfer.files[0];
              if (file) {
                imageInputRef.current.files = e.dataTransfer.files;
                handleImageChange({ target: { files: e.dataTransfer.files } });
              }
            }}
          >
            <p>이미지를 클릭하거나 드래그 앤 드롭하세요</p>
            <input
              type="file"
              accept="image/*"
              ref={imageInputRef}
              onChange={handleImageChange}
              style={{ display: 'none' }}
            />
          </div>

          {/* 🖼️ 이미지 미리보기 */}
          {previewUrl && (
            <div className="image-preview">
              <img src={previewUrl} alt="업로드된 음식" />
            </div>
          )}

          {/* 분석 중 메시지 */}
          {loading && <p className="loading-message">🔍 분석 중입니다...</p>}

          {analysisResult && (
            <>
              <div className="result-card">
                <div className="predicted-class">
                  🍽️ 예측 결과: <strong>{analysisResult.predictedClass}</strong>
                </div>
                <div className="confidence-score">
                  신뢰도: {(analysisResult.confidence * 100).toFixed(2)}%
                </div>
              </div>

              {/* 2열 레이아웃 - 영양 정보 왼쪽, 차트 오른쪽 */}
              <div className="nutrition-and-chart">
                <div className="nutrition-card">
                  <h3>🥗 영양 정보</h3>
                  <ul className="nutrition-list">
                    <li><strong>칼로리:</strong> {analysisResult.calories} kcal</li>
                    <li><strong>탄수화물:</strong> {analysisResult.nut_carb} g</li>
                    <li><strong>단백질:</strong> {analysisResult.nut_pro} g</li>
                    <li><strong>지방:</strong> {analysisResult.nut_fat} g</li>
                  </ul>
                </div>

                <div className="chart-container">
                  <Pie data={chartData} />
                </div>
              </div>
            </>
          )}

          {/* 추천 버튼 */}
          <div className="button-group">
            <button
              onClick={handleRecommendation}
              className="button secondary"
            >
              식당 추천
            </button>
            <button
              onClick={() => navigate('/meal-recommend')}
              className="button secondary"
            >
              식단 추천
            </button>
          </div>
        </div>
      </div>
    </>
  );
}
