import React from 'react';
import {
  BarChart, Bar, XAxis, YAxis, Tooltip, Legend, ResponsiveContainer,
} from 'recharts';

export default function MealChart({ meals }) {
  // ‚õî Îç∞Ïù¥ÌÑ∞ ÏóÜÍ±∞ÎÇò ÏòÅÏñëÏÜå ÌïÑÎìú Îπ†Ï°åÏùÑ Îïå Î∞©Ïñ¥
  if (
    !meals || meals.length === 0 ||
    !('calories' in meals[0] || 'carb' in meals[0] || 'protein' in meals[0] || 'fat' in meals[0])
  ) {
    return <div>üì≠ Ï∞®Ìä∏Î•º Î†åÎçîÎßÅÌï† Ïàò ÏóÜÏäµÎãàÎã§. ÏòÅÏñë Ï†ïÎ≥¥Í∞Ä Ìè¨Ìï®Îêú Îç∞Ïù¥ÌÑ∞Î•º Ï†úÍ≥µÌï¥Ï£ºÏÑ∏Ïöî.</div>;
  }

  const chartData = meals.map((meal, idx) => ({
    name: meal.meal_date || `ÏãùÎã®${idx + 1}`,
    calories: Number(meal.calories || 0),
    carb: Number(meal.carb || meal.nut_carb || 0),
    protein: Number(meal.protein || meal.nut_pro || 0),
    fat: Number(meal.fat || meal.nut_fat || 0),
  }));

  return (
    <div className="meal-chart-container">
      <h4>üìä ÏòÅÏñë Ï†ïÎ≥¥ Ï∞®Ìä∏</h4>
      <div style={{ width: '100%', height: 300 }}>
        {chartData.length > 0 && (
          <ResponsiveContainer>
            <BarChart data={chartData}>
              <XAxis dataKey="name" />
              <YAxis />
              <Tooltip />
              <Legend />
              <Bar dataKey="calories" fill="#8884d8" name="ÏπºÎ°úÎ¶¨" />
              <Bar dataKey="carb" fill="#82ca9d" name="ÌÉÑÏàòÌôîÎ¨º" />
              <Bar dataKey="protein" fill="#ffc658" name="Îã®Î∞±Ïßà" />
              <Bar dataKey="fat" fill="#ff7f7f" name="ÏßÄÎ∞©" />
            </BarChart>
          </ResponsiveContainer>
        )}
      </div>
    </div>
  );
}
