import React, { useState, useEffect } from "react";
import axios from "axios";

const ChatTest = () => {
  const [message, setMessage] = useState("");
  const [responses, setResponses] = useState([]);
  const [userInfo, setUserInfo] = useState(null);

  // 페이지 로드 시 세션에서 사용자 정보 가져오기
  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      setUserInfo(JSON.parse(storedUser)); // 세션 정보 가져오기
    }
  }, []);
  const handleSend = async () => {
    if (!message.trim()) return;

    try {
      // 세션 정보 확인
      if (!userInfo) {
        alert("사용자 정보가 없습니다.");
        return;
      }

      // 세션 기반이므로 userId 전달 안 해도 됨
      const res = await axios.post(
        "http://localhost:8000/api/rag/generate",
        { question: message,
          user_id: userInfo.user_id,
         },
        { withCredentials: true } // 세션 쿠키 포함
      );

      setResponses((prev) => [
        ...prev,
        { role: "user", content: message },
        { role: "bot", content: res.data.answer },
      ]);
      setMessage("");
    } catch (err) {
      console.error("챗봇 응답 실패:", err);
    }
  };

  return (
    <div className="max-w-xl mx-auto p-4">
      <div className="border p-4 rounded h-96 overflow-y-scroll mb-2 bg-gray-100">
        {responses.map((msg, idx) => (
          <div
            key={idx}
            className={`mb-2 ${msg.role === "user" ? "text-right" : "text-left"}`}
          >
            <span
              className={`inline-block px-3 py-1 rounded ${
                msg.role === "user" ? "bg-blue-200" : "bg-green-200"
              }`}
            >
              {msg.content}
            </span>
          </div>
        ))}
      </div>
      <div className="flex gap-2">
        <input
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="메시지를 입력하세요..."
          className="flex-grow border px-3 py-2 rounded"
        />
        <button onClick={handleSend} className="bg-blue-500 text-white px-4 py-2 rounded">
          전송
        </button>
      </div>
    </div>
  );
};

export default ChatTest;
