import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './css/signup2.css'; // Ïä§ÌÉÄÏùº ÌååÏùº

export default function Signup2() {
    const navigate = useNavigate();

    const [formData, setFormData] = useState({
        user_id: null,
        gender: '',
        age: '',
        height: '',
        user_weight: '',
        user_address: '',
        health_goal: ''
    });

    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');

    // üëâ Ïª¥Ìè¨ÎÑåÌä∏ ÎßàÏö¥Ìä∏ Ïãú user_id Î∂àÎü¨Ïò§Í∏∞
    useEffect(() => {
        const storedUserId = localStorage.getItem('user_id');
        if (storedUserId && !isNaN(storedUserId)) {
            setFormData(prev => ({
                ...prev,
                user_id: parseInt(storedUserId, 10)
            }));
        } else {
            setError('Ïú†Ìö®ÌïòÏßÄ ÏïäÏùÄ ÏÇ¨Ïö©Ïûê IDÏûÖÎãàÎã§. Îã§Ïãú Î°úÍ∑∏Ïù∏Ìï¥Ï£ºÏÑ∏Ïöî.');
        }
    }, []);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleAddressSearch = () => {
        new window.daum.Postcode({
            oncomplete: function (data) {
                const address = data.address;
                setFormData(prev => ({ ...prev, user_address: address }));
            }
        }).open();
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        setSuccess('');

        if (!formData.user_id) {
            setError('ÌöåÏõê Ï†ïÎ≥¥Í∞Ä Ïú†Ìö®ÌïòÏßÄ ÏïäÏäµÎãàÎã§. Îã§Ïãú Î°úÍ∑∏Ïù∏Ìï¥Ï£ºÏÑ∏Ïöî.');
            return;
        }

        try {
            await axios.post('http://localhost:8080/users/details', formData);
            setSuccess('Í±¥Í∞ï Ï†ïÎ≥¥Í∞Ä ÏÑ±Í≥µÏ†ÅÏúºÎ°ú Ï†ÄÏû•ÎêòÏóàÏäµÎãàÎã§!');
            setTimeout(() => navigate('/login'), 1000);
        } catch (err) {
            setError('Ï†ïÎ≥¥ Ï†ÄÏû• Ïã§Ìå®: Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî.');
            console.error(err);
        }
    };

    return (
        <div className="container">
            <div className="text-center">
                <h1>Ï∂îÍ∞Ä Í±¥Í∞ï Ï†ïÎ≥¥ ÏûÖÎ†•</h1>
            </div>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label>ÏÑ±Î≥Ñ</label>
                    <select
                    name="gender"
                    value={formData.gender}
                    onChange={handleChange}
                    className="form-control"
                    required
                    >
                      <option value="">ÏÑ†ÌÉù</option>
                      <option value="1">ÎÇ®ÏÑ±</option>
                      <option value="2">Ïó¨ÏÑ±</option>
                      <option value="3">Í∏∞ÌÉÄ</option>
                    </select>
                </div>

                <div className="form-group">
                    <label>ÎÇòÏù¥</label>
                    <input
                        type="number"
                        name="age"
                        value={formData.age}
                        onChange={handleChange}
                        className="form-control"
                        required
                    />
                </div>

                <div className="form-group">
                    <label>ÌÇ§(cm)</label>
                    <input
                        type="number"
                        name="height"
                        value={formData.height}
                        onChange={handleChange}
                        className="form-control"
                        required
                    />
                </div>

                <div className="form-group">
                    <label>Î™∏Î¨¥Í≤å(kg)</label>
                    <input
                        type="number"
                        name="user_weight"
                        value={formData.user_weight}
                        onChange={handleChange}
                        className="form-control"
                        required
                    />
                </div>

                <div className="form-group">
                    <label>Ï£ºÏÜå</label>
                    <div className="input-group">
                        <input
                            type="text"
                            name="user_address"
                            value={formData.user_address}
                            onChange={handleChange}
                            className="form-control"
                            readOnly
                        />
                        <div className="input-group-append">
                            <button
                                type="button"
                                className="btn btn-outline-secondary"
                                onClick={handleAddressSearch}
                            >
                                Ï£ºÏÜå Í≤ÄÏÉâ
                            </button>
                        </div>
                    </div>
                </div>

                <select
                  name="health_goal"
                  value={formData.health_goal}
                  onChange={handleChange}
                  className="form-control"
                  required>
                    <option value="">ÏÑ†ÌÉù</option>
                    <option value="1">Ï≤¥Ï§ë Í∞êÎüâ</option>
                    <option value="2">Í∑ºÏú° Ï¶ùÍ∞Ä</option>
                    <option value="3">Í±¥Í∞ï Ïú†ÏßÄ</option>
                    </select>


                {error && <div className="alert alert-danger mt-3">{error}</div>}
                {success && <div className="alert alert-success mt-3">{success}</div>}

                <button type="submit" className="btn btn-primary mt-4 w-100">Ï†úÏ∂úÌïòÍ∏∞</button>
            </form>
        </div>
    );
}
