import React, { useEffect, useState } from 'react';
import axios from 'axios';
import NavBar from '../components/NavBar';
import DatePicker from 'react-datepicker';
import { ko } from 'date-fns/locale';
import { saveAs } from 'file-saver';
import 'react-datepicker/dist/react-datepicker.css';
import './css/admin.css';
import './css/main.css';
import './css/userlog.css';

const BASE_URL = process.env.REACT_APP_BASE_URL;
const PAGE_SIZE = 10;

export default function UserLogsPage() {
  const [logs, setLogs] = useState([]);
  const [totalLogs, setTotalLogs] = useState(0);
  const [currentPage, setCurrentPage] = useState(1);
  const [searchEmail, setSearchEmail] = useState('');
  const [statusFilter, setStatusFilter] = useState('ALL');
  const today = new Date();
  const startOfMonth = new Date(today.getFullYear(), today.getMonth(), 1);
  const endOfMonth = new Date(today.getFullYear(), today.getMonth() + 1, 0);
  const [startDate, setStartDate] = useState(startOfMonth);
  const [endDate, setEndDate] = useState(endOfMonth);

  const fetchLogs = async (page) => {
    try {
      const res = await axios.get(`${BASE_URL}/users/admin/logs?page=${page}&size=${PAGE_SIZE}`, {
        withCredentials: true,
      });
      setLogs(res.data.logs || []);
      setTotalLogs(res.data.total || 0);
    } catch (err) {
      console.error('‚ùå Î°úÍ∑∏ Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:', err);
    }
  };

  useEffect(() => {
    fetchLogs(currentPage);
  }, [currentPage]);

  // üîç ÌïÑÌÑ∞ÎßÅ
  const filteredLogs = logs.filter(log => {
    const emailMatch = log.email?.toLowerCase().includes(searchEmail.toLowerCase());
    const statusMatch = statusFilter === 'ALL' || log.loginStatus === statusFilter;
    const date = new Date(log.regDate.replace(' ', 'T'));
    const dateMatch =
      (!startDate || date >= startDate) &&
      (!endDate || date <= new Date(endDate.getTime() + 86400000));
    return emailMatch && statusMatch && dateMatch;
  });

  const totalPages = Math.ceil(totalLogs / PAGE_SIZE);

  // üì• CSV Îã§Ïö¥Î°úÎìú
  const handleDownloadCSV = () => {
    const header = "ÌöåÏõêID,Email,IP,Î°úÍ∑∏Ïù∏ÏùºÏãú,Î°úÍ∑∏ÏïÑÏõÉÏùºÏãú,ÏÉÅÌÉú,Ïã§Ìå®Ïù¥Ïú†\n";
    const csv = filteredLogs.map(log =>
      `${log.id},"${log.email}",${log.ipAddress},${log.loginTime},${log.logoutTime},${log.loginStatus},${log.failureReason}`
    ).join("\n");

    const blob = new Blob([header + csv], { type: "text/csv;charset=utf-8" });
    saveAs(blob, "user_logs.csv");
  };

  return (
    <>
      <NavBar />
      <div className="admin-wrapper">
        <div className="admin-inner">
          <h2 className="section-title">ÏÇ¨Ïö©Ïûê Î°úÍ∑∏ Ï°∞Ìöå</h2>

          <div className="search-row">
            <DatePicker
                selected={startDate}
                onChange={(date) => setStartDate(date)}
                dateFormat="yyyy-MM-dd"
                placeholderText="ÏãúÏûëÏùºÏûê"
                locale={ko}
                className="date-input"
            />

            <DatePicker
                selected={endDate}
                onChange={(date) => setEndDate(date)}
                dateFormat="yyyy-MM-dd"
                placeholderText="Ï¢ÖÎ£åÏùºÏûê"
                locale={ko}
                className="date-input"
            />

            <select
                className="status-select"
                value={statusFilter}
                onChange={(e) => setStatusFilter(e.target.value)}
            >
                <option value="ALL">Ï†ÑÏ≤¥</option>
                <option value="SUCCESS">ÏÑ±Í≥µ</option>
                <option value="FAIL">Ïã§Ìå®</option>
            </select>
            <input
                type="text"
                className="search-input"
                placeholder="Ïù¥Î©îÏùº Í≤ÄÏÉâ"
                value={searchEmail}
                onChange={(e) => setSearchEmail(e.target.value)}
            />
          </div>

            {/* üëâ Ïó¨Í∏∞! Îã§Ïö¥Î°úÎìú Î≤ÑÌäºÍ≥º ÌÉÄÏù¥ÌãÄ */}
            <div className="table-header">
                <button onClick={handleDownloadCSV} className="btn csv-button">üì• CSV</button>
            </div>
          <table className="admin-table">
            <colgroup>
                <col style={{ width: '6%' }} />
                <col style={{ width: '14%' }} />
                <col style={{ width: '12%' }} />
                <col style={{ width: '15%' }} />
                <col style={{ width: '15%' }} />
                <col style={{ width: '8%' }} />
                <col style={{ width: '30%' }} />
            </colgroup>
            <thead>
              <tr>
                <th>Ïú†Ï†ÄID</th>
                <th>Ïù¥Î©îÏùº</th>
                <th>IP</th>
                <th>Î°úÍ∑∏Ïù∏ÏùºÏãú</th>
                <th>Î°úÍ∑∏ÏïÑÏõÉÏùºÏãú</th>    
                <th>ÏÉÅÌÉú</th>
                <th>Ïã§Ìå®Ïù¥Ïú†</th>
              </tr>
            </thead>
            <tbody>
              {filteredLogs.map((log) => (
                <tr key={log.id}>
                  <td>{log.id}</td>
                  <td>{log.email || ''}</td>
                  <td>{log.ipAddress}</td>
                  <td>{log.loginTime || '-'}</td>
                  <td>{log.logoutTime || '-'}</td>
                  <td>{log.loginStatus || 'ÌôïÏù∏ ÏïàÎê®'}</td>
                  <td>{log.failureReason || '-'}</td>
                </tr>
              ))}
            </tbody>
          </table>

          <div className="pagination">
            {[...Array(totalPages)].map((_, i) => (
              <button
                key={i}
                className={`page-button ${currentPage === i + 1 ? 'active' : ''}`}
                onClick={() => setCurrentPage(i + 1)}
              >
                {i + 1}
              </button>
            ))}
          </div>
        </div>
      </div>
    </>
  );
}
