import React, { useEffect } from 'react';

export default function KakaoMap({ stores }) {
  useEffect(() => {
    const loadKakaoMapScript = () => {
      if (window.kakao && window.kakao.maps) {
        initMap();
        return;
      }

      const script = document.createElement("script");
      script.src = "//dapi.kakao.com/v2/maps/sdk.js?appkey=YOUR_APP_KEY&autoload=false&libraries=services";
      script.async = true;
      script.onload = () => {
        window.kakao.maps.load(() => {
          initMap();
        });
      };
      document.head.appendChild(script);
    };

    const initMap = () => {
      if (!stores || stores.length === 0) return;

      const container = document.getElementById('map');
      const options = {
        center: new window.kakao.maps.LatLng(37.5665, 126.9780),
        level: 5
      };
      const map = new window.kakao.maps.Map(container, options);
      const geocoder = new window.kakao.maps.services.Geocoder();

      stores.forEach((store) => {
        geocoder.addressSearch(store.address, function (result, status) {
          if (status === window.kakao.maps.services.Status.OK) {
            const coords = new window.kakao.maps.LatLng(result[0].y, result[0].x);
            const marker = new window.kakao.maps.Marker({
              map: map,
              position: coords
            });

            const info = new window.kakao.maps.InfoWindow({
              content: `<div style="padding:5px;">${store.name}</div>`
            });

            marker.setMap(map);
            window.kakao.maps.event.addListener(marker, 'click', () => {
              info.open(map, marker);
            });
          }
        });
      });
    };

    loadKakaoMapScript();
  }, [stores]);

  return <div id="map" style={{ width: '100%', height: '400px', marginTop: '20px' }} />;
}
