import React, { useEffect, useState } from 'react';
import axios from 'axios';

function ChatbotPage() {
  const [loading, setLoading] = useState(true);
  const [chatReady, setChatReady] = useState(false);
  const [error, setError] = useState('');
  const [user, setUser] = useState(null);

  useEffect(() => {
    const prepareRAG = async () => {
      try {
        // 1. ÏÑ∏ÏÖòÏóêÏÑú Ïú†Ï†Ä Ï†ïÎ≥¥ ÌôïÏù∏
        const sessionRes = await axios.get("http://localhost:8080/ses", { withCredentials: true })
        const userData = sessionRes.data;
        setUser(userData);

        // 2. RAG ÏÉùÏÑ± Ï§ÄÎπÑ ÏöîÏ≤≠
        if (userData) { // Ïú†Ï†Ä Îç∞Ïù¥ÌÑ∞Í∞Ä Ïú†Ìö®Ìï† ÎïåÎßå RAG ÏöîÏ≤≠
          const ragRes = await axios.post(
            "http://localhost:8000/chat/chat",
            { user_id: userData.user_id },
            { withCredentials: true }
          );

          console.log('RAG ÏÉùÏÑ± Í≤∞Í≥º:', ragRes.data);
          setChatReady(true);
        }
      } catch (err) {
        console.error(err);
        setError('Ï±óÎ¥á Ï§ÄÎπÑ Ï§ë Ïò§Î•ò Î∞úÏÉù');
      } finally {
        setLoading(false);
      }
    };

    prepareRAG();
  }, []); // Ïù¥ Î∂ÄÎ∂ÑÏùÄ ÏÑ∏ÏÖòÏùÑ Ï¥àÍ∏∞ Î°úÎî© Ïãú Ìïú Î≤àÎßå Ìò∏Ï∂úÌï©ÎãàÎã§.

  if (loading) return <div>Ï±óÎ¥á Ï§ÄÎπÑ Ï§ëÏûÖÎãàÎã§...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="chatbot-container">
      <h2>üß† Ï±óÎ¥áÍ≥º ÎåÄÌôîÌïòÍ∏∞</h2>
      {chatReady ? (
        <ChatInterface user={user} />
      ) : (
        <div>Ï±óÎ¥áÏùÑ ÏÇ¨Ïö©Ìï† Ïàò ÏóÜÏäµÎãàÎã§.</div>
      )}
    </div>
  );
}

// Ïã§Ï†ú Ï±ó Ïù∏ÌÑ∞ÌéòÏù¥Ïä§ (Îã®ÏàúÌôî)
function ChatInterface({ user }) {
  const [message, setMessage] = useState('');
  const [chat, setChat] = useState([]);

  const sendMessage = async () => {
    if (!message) return;

    const res = await axios.post(
      "http://localhost:8000/chat/chat",
      {
        user_id: user.user_id,
        question: message
      },
      { withCredentials: true }
    );
    setChat([...chat, { question: message, answer: res.data.answer }]);
    setMessage('');
  };

  return (
    <div>
      <div>
        {chat.map((item, idx) => (
          <div key={idx}>
            <strong>üë§:</strong> {item.question}<br />
            <strong>ü§ñ:</strong> {item.answer}
          </div>
        ))}
      </div>
      <input
        value={message}
        onChange={(e) => setMessage(e.target.value)}
        placeholder="Î©îÏãúÏßÄÎ•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî..."
      />
      <button onClick={sendMessage}>Î≥¥ÎÇ¥Í∏∞</button>
    </div>
  );
}

export default ChatbotPage;
