import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import MainPage from './MainPage';
import MyPage from './MyPage';
import ChangePassword from './ChangePassword';
import Login from './Login';
import SignupForm from './SignupForm';
import { GoogleOAuthProvider } from '@react-oauth/google';
import UserDetailsForm from './UserDetailsForm';
import AdminPage from './AdminPage';
import NavBar from './NavBar';
import FindAccount from './FindAccount';
import ResetPassword from './ResetPassword';
import { ToastContainer } from 'react-toastify';
import UserMealSearch from './UserMealSearch';
import ChatTest from "./ChatTest"; // 경로는 실제 ChatTest.jsx 위치에 맞게 수정
import axios from 'axios'; 

function App() {
  const [user, setUser] = useState(null);  // 유저 상태를 추가합니다.
  
  // 세션 확인 및 유저 정보 가져오는 useEffect
  useEffect(() => {
    axios.get('http://localhost:8080/ses', { withCredentials: true })
      .then(response => {
        if (response.data) {
          setUser(response.data);  // 유저 정보를 상태로 설정
        } else {
          setUser(null);  // 유저 정보가 없다면 null로 설정
        }
      })
      .catch(error => {
        console.error('세션 확인 오류:', error);
      });
  }, []);

  return (
    
    <GoogleOAuthProvider clientId="758964500028-3l2f9avbost20tq4ri7nr4nfed4fd2l3.apps.googleusercontent.com">

      <Router>
      <div>
        {/* ToastContainer를 여기에 추가 */}
        <ToastContainer 
          position="top-right"
          autoClose={1500} 
          hideProgressBar={false}
          newestOnTop={false}
          closeOnClick
          rtl={false}
          pauseOnFocusLoss
          draggable
          pauseOnHover
          theme="light"
        />
          <Routes>
            <Route path="/" element={<MainPage />} />
            <Route path="/navbar" element={<NavBar />} />
            <Route path="/login" element={<Login />} />
            <Route path="/signup" element={<SignupForm />} />
            <Route path="/user-details" element={<UserDetailsForm />} />
            <Route path="/users/admin" element={<AdminPage />} />
            <Route path="/mypage" element={<MyPage />} />
            <Route path="/findaccount" element={<FindAccount />} />
            <Route path="/change-password" element={<ChangePassword />} />
            <Route path="/meal-plan" element={<UserMealSearch />} />
            <Route path="/reset-password" element={<ResetPassword />} />
            <Route path="/chat-test" element={<ChatTest user={user} />} />
          </Routes>
        </div>
      </Router>
    </GoogleOAuthProvider>
  );
}

export default App;
