import React, { useState } from 'react';
import axios from 'axios';
import NavBar from './NavBar';
import './css/meal-recommend.css';
import './css/main.css';

export default function MealRecommendation() {
    const getToday = () => {
        const today = new Date();
        return today.toISOString().split('T')[0];
    };

    const getEndOfMonth = () => {
    const date = new Date();
    const end = new Date(date.getFullYear(), date.getMonth() + 1, 1);
        return end.toISOString().split('T')[0];
    };

    const [startDate, setStartDate] = useState(getToday());
    const [endDate, setEndDate] = useState(getEndOfMonth());
    const [mealTypes, setMealTypes] = useState([]);
    const [recommendedMeals, setRecommendedMeals] = useState([]);
    const [loading, setLoading] = useState(false); // ‚úÖ Î°úÎî© ÏÉÅÌÉú Ï∂îÍ∞Ä

    const handleCheckboxChange = (type) => {
        if (type === 'all') {
            const isAllSelected = mealTypes.includes('breakfast') && mealTypes.includes('lunch') && mealTypes.includes('dinner');
            setMealTypes(isAllSelected ? [] : ['breakfast', 'lunch', 'dinner']);
        } else {
            setMealTypes((prev) =>
                prev.includes(type) ? prev.filter((t) => t !== type) : [...prev, type]
            );
        }
    };

    const handleRecommend = async () => {
        try {
          setLoading(true); // ‚úÖ Î°úÎî© ÏãúÏûë
            // 'all'ÏùÄ Ï†úÍ±∞ÌïòÍ≥† ÎÑòÍπÄ
            const filteredTypes = mealTypes.filter((type) => type !== 'all');
            const payload = new URLSearchParams({
                start: startDate,
                end: endDate,
                types: filteredTypes.join(',')
            });

            const response = await axios.post('http://localhost:8080/analyze/recommend', payload, {
                headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
                withCredentials: true
            });

            if (response.status === 200) {
                alert('Ï∂îÏ≤úÏôÑÎ£å.!');
            }
            setRecommendedMeals(response.data.result);
        } catch (error) {
            console.error('Ï∂îÏ≤ú Ïã§Ìå®:', error);
        } finally {
          setLoading(false); // ‚úÖ Î°úÎî© Ï¢ÖÎ£å
      }
    };


  return (
    <>
    <NavBar />
    <div className="recommend-wrapper">
      <div className="recommend-inner">
        <h2 className="title">üìÖ ÎßûÏ∂§ ÏãùÎã® Ï∂îÏ≤ú</h2>

        <div className="form-row">
          <div className="form-group-inline">
            <label>Í∏∞Í∞Ñ ÏÑ†ÌÉù</label>
            <input type="date" value={startDate} onChange={(e) => setStartDate(e.target.value)} className="date-input" />
            <span className="tilde">~</span>
            <input type="date" value={endDate} onChange={(e) => setEndDate(e.target.value)} className="date-input" />
          </div>

          <div className="form-group-inline meal-type-group">
            <label>ÏãùÏÇ¨ ÏÑ†ÌÉù</label>
            <label><input type="checkbox" checked={mealTypes.includes('breakfast') && mealTypes.includes('lunch') && mealTypes.includes('dinner')} onChange={() => handleCheckboxChange('all')} /> Ï†ÑÏ≤¥</label>
            <label><input type="checkbox" checked={mealTypes.includes('breakfast')} onChange={() => handleCheckboxChange('breakfast')} /> Ï°∞Ïãù</label>
            <label><input type="checkbox" checked={mealTypes.includes('lunch')} onChange={() => handleCheckboxChange('lunch')} /> Ï§ëÏãù</label>
            <label><input type="checkbox" checked={mealTypes.includes('dinner')} onChange={() => handleCheckboxChange('dinner')} /> ÏÑùÏãù</label>
          </div>

          <button onClick={handleRecommend} className="btn btn-primary" disabled={loading}>
              {loading ? 'üçΩ Ï∂îÏ≤úÏ§ë...' : 'üç± Ï∂îÏ≤ú Î∞õÍ∏∞'} {/* ‚úÖ ÌÖçÏä§Ìä∏ Î≥ÄÍ≤Ω */}
          </button>
        </div>

        {loading && (
            <p style={{ marginTop: '10px', fontWeight: '500', color: '#555' }}>
                Ï∂îÏ≤ú Í≤∞Í≥ºÎ•º ÏÉùÏÑ± Ï§ëÏûÖÎãàÎã§. Ïû†ÏãúÎßå Í∏∞Îã§Î†§Ï£ºÏÑ∏Ïöî...
            </p>
        )}

        <div className="table-scroll-container">
          <table className="result-table">
            <thead>
              <tr>
                <th className="col-narrow">ÎÇ†Ïßú</th>
                <th className="col-narrow">ÏãùÏÇ¨Íµ¨Î∂Ñ</th>
                <th className="col-wide">Ï∂îÏ≤úÎ©îÎâ¥</th>
              </tr>
            </thead>
            <tbody>
              {recommendedMeals.map((meal, index) => (
                <tr key={index}>
                  <td>{meal.date}</td>
                  <td>{meal.type}</td>
                  <td>{meal.menu}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
    </>
  );
}
