import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import NavBar from './NavBar';
import { GoogleLogin } from '@react-oauth/google';
import './css/signup.css';

export default function Signup() {
    const BASE_URL = process.env.REACT_APP_BASE_URL;
    const [form, setForm] = useState({
        user_name: '',
        email: '',
        user_password: '',
        confirmPassword: '',
        phone: ''
    });

    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');
    const navigate = useNavigate();

    const handleChange = (e) => {
        const { name, value } = e.target;
        setForm(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        setSuccess('');

        if (form.user_password !== form.confirmPassword) {
            setError('ÎπÑÎ∞ÄÎ≤àÌò∏Í∞Ä ÏùºÏπòÌïòÏßÄ ÏïäÏäµÎãàÎã§.');
            return;
        }

        try {
            const response = await axios.post(
                `${BASE_URL}/users/signup`,
                {
                    user_name: form.user_name,
                    email: form.email,
                    user_password: form.user_password,
                    phone: form.phone
                },
                {
                    headers: {
                        'Content-Type': 'application/json',
                    }
                }
            );

            if (response.status === 200 || response.status === 201) {
                const userId = response.data.user_id;
                console.error(userId);
                if (!userId) {
                    setError('ÏÑúÎ≤ÑÎ°úÎ∂ÄÌÑ∞ ÏÇ¨Ïö©Ïûê IDÎ•º Î∞õÏßÄ Î™ªÌñàÏäµÎãàÎã§.');
                    return;
                }

                localStorage.setItem("user_id", userId);
                setSuccess('ÌöåÏõêÍ∞ÄÏûÖÏù¥ ÏôÑÎ£åÎêòÏóàÏäµÎãàÎã§!');
                setTimeout(() => {
                    navigate('/user-details');
                }, 1000);
            }
        } catch (err) {
            console.error(err);
            setError('ÌöåÏõêÍ∞ÄÏûÖ Ïã§Ìå®: ' + (err.response?.data?.message || 'Ïïå Ïàò ÏóÜÎäî Ïò§Î•ò'));
        }
    };

    const handleGoogleSignup = async (credentialResponse) => {
        try {
            const res = await axios.post(`${BASE_URL}/users/google`, {
                token: credentialResponse.credential,
            });

            const userId = res.data.user_id;

            if (!userId) {
                alert("ÏÑúÎ≤ÑÎ°úÎ∂ÄÌÑ∞ ÏÇ¨Ïö©Ïûê IDÎ•º Î∞õÏßÄ Î™ªÌñàÏäµÎãàÎã§.");
                return;
            }

            localStorage.setItem("user_id", userId);
            alert("Íµ¨Í∏Ä ÌöåÏõêÍ∞ÄÏûÖ ÏÑ±Í≥µ!");
            navigate('/user-details');
        } catch (err) {
            console.error(err);
            alert("Íµ¨Í∏Ä ÌöåÏõêÍ∞ÄÏûÖ Ïã§Ìå®");
        }
    };

    return (
        <>
            <NavBar />        
            <div className="container">
            <div className="text-center">
     <h1>üå± Start your healthy eating üßë‚Äç‚öï</h1>
 </div>
                <form onSubmit={handleSubmit}>
                    <div className="form-group">
                        <input
                            type="text"
                            className="form-control"
                            placeholder="Ïù¥Î¶Ñ*"
                            name="user_name"
                            value={form.user_name}
                            onChange={handleChange}
                            required
                        />
                    </div>
                    <div className="form-group">
                        <input
                            type="email"
                            className="form-control"
                            placeholder="Ïù¥Î©îÏùº*"
                            name="email"
                            value={form.email}
                            onChange={handleChange}
                            required
                        />
                    </div>
                    <div className="form-group">
                        <input
                            type="password"
                            className="form-control"
                            placeholder="ÎπÑÎ∞ÄÎ≤àÌò∏*"
                            name="user_password"
                            value={form.user_password}
                            onChange={handleChange}
                            required
                        />
                    </div>
                    <div className="form-group">
                        <input
                            type="password"
                            className="form-control"
                            placeholder="ÎπÑÎ∞ÄÎ≤àÌò∏ ÌôïÏù∏*"
                            name="confirmPassword"
                            value={form.confirmPassword}
                            onChange={handleChange}
                            required
                        />
                    </div>
                    <div className="form-group">
                        <input
                            type="tel"
                            className="form-control"
                            placeholder="Ï†ÑÌôîÎ≤àÌò∏*"
                            name="phone"
                            value={form.phone}
                            onChange={handleChange}
                            required
                        />
                    </div>

                    {error && <div className="error-message">{error}</div>}
                    {success && <div className="success-message">{success}</div>}

                    <button type="submit" className="btn btn-primary">Í≥ÑÏÜç</button>

                    <div className="terms-text">
                        ÌöåÏõêÍ∞ÄÏûÖ Ïãú <Link to="/terms">Ïù¥Ïö©ÏïΩÍ¥Ä</Link>Í≥º <br /><Link to="/privacy">Í∞úÏù∏Ï†ïÎ≥¥ Î≥¥Ìò∏Ï†ïÏ±Ö</Link>Ïóê ÎèôÏùòÌïòÎäî Í≤ÉÏúºÎ°ú Í∞ÑÏ£ºÎê©ÎãàÎã§.
                    </div>

                    <hr />
                    <GoogleLogin
                        onSuccess={handleGoogleSignup}
                        onError={() => alert("Íµ¨Í∏Ä Î°úÍ∑∏Ïù∏ Ïã§Ìå®")}
                    />
                </form>
            </div>
        </>
    );
}
