import React, { useState } from 'react';
import axios from 'axios';
import NavBar from './NavBar';
import { toast } from 'react-toastify';
import './css/meal-recommend.css';
import './css/main.css';
import { useParams } from 'react-router-dom';


export default function MealRecommendation() {
    const { foodName } = useParams();
    const [location, setLocation] = useState();
    const [recommendedStore, setRecommendedStore] = useState([]);
    const [loading, setLoading] = useState(false); // ‚úÖ Î°úÎî© ÏÉÅÌÉú Ï∂îÍ∞Ä

    const handleRecommend = async () => {
        try {
          setLoading(true); // ‚úÖ Î°úÎî© ÏãúÏûë
            // 'all'ÏùÄ Ï†úÍ±∞ÌïòÍ≥† ÎÑòÍπÄ
            const payload = new URLSearchParams({
                foodName: foodName,
                location: location,
            });

            const response = await axios.post('http://localhost:8080/analyze/store', payload, {
                headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
                withCredentials: true
            });

            if (response.status === 200) {
                toast.success('Ï∂îÏ≤ú ÏôÑÎ£å!');
            }
            setRecommendedStore(response.data);
        } catch (error) {
            console.error('Ï∂îÏ≤ú Ïã§Ìå®:', error);
        } finally {
          setLoading(false); // ‚úÖ Î°úÎî© Ï¢ÖÎ£å
      }
    };


  return (
    <>
    <NavBar />
    <div className="recommend-wrapper">
        <div className="recommend-inner">
            <h2 className="title">üìÖ {foodName} ÎßûÏ∂§ ÏãùÎãπ Ï∂îÏ≤ú</h2>
                <input placeholder="Ï£ºÏÜåÎ•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî!" value={location} onChange={(e) => setLocation(e.target.value)} />
                <button onClick={handleRecommend} className="btn btn-primary" disabled={loading}>
                    {loading ? 'üçΩ Ï∂îÏ≤úÏ§ë...' : 'üç± Ï∂îÏ≤ú Î∞õÍ∏∞'}  {/* ‚úÖ ÌÖçÏä§Ìä∏ Î≥ÄÍ≤Ω */}
                </button>
            </div>

            {loading && (
                <p style={{ marginTop: '10px', fontWeight: '500', color: '#555' }}>
                    Ï∂îÏ≤ú Í≤∞Í≥ºÎ•º ÏÉùÏÑ± Ï§ëÏûÖÎãàÎã§. Ïû†ÏãúÎßå Í∏∞Îã§Î†§Ï£ºÏÑ∏Ïöî...
                </p>
            )}
        </div>
        <div className="table-scroll-container">
            <table className="result-table">
            <thead>
                <tr>
                <th className="col-narrow">ÏÉÅÌò∏Î™Ö</th>
                <th className="col-narrow">ÎßÅÌÅ¨</th>
                {/* <th className="col-wide">Ï†ÑÌôîÎ≤àÌò∏</th>
                <th className="col-narrow">Ï£ºÏÜå</th>
                <th className="col-narrow">ÏòÅÏóÖÏãúÍ∞Ñ</th>
                <th className="col-narrow">Î¶¨Î∑∞</th> */}
                </tr>
            </thead>
            <tbody>
                {recommendedStore.map((meal, index) => (
                <tr key={index}>
                    <td>{meal.name}</td>
                    <td>{meal.address}</td>
                </tr>
                ))}
            </tbody>
            </table>
        </div>
    </>
  );
}
