import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './css/main.css';

export default function MainPage() {
  const [menuOpen, setMenuOpen] = useState(false);
  const [darkMode, setDarkMode] = useState(false);

  useEffect(() => {
    document.documentElement.setAttribute('data-theme', darkMode ? 'dark' : 'light');
  }, [darkMode]);

  return (
    <>
      <nav className="glass-navbar">
        <div className="navbar-container">
          <div className="navbar-left">
            <Link to="/" className="logo">üçΩ FoodyChat</Link>
          </div>

          <div className="navbar-center desktop-menu">
            <Link to="/chatbot" className="nav-link">ü§ñ Ï±óÎ¥á</Link>
            <Link to="/image-analysis" className="nav-link">üç± Ïù¥ÎØ∏ÏßÄ Î∂ÑÏÑù</Link>
          </div>

          <div className="navbar-right desktop-menu">
            <Link to="/login" className="nav-button">Î°úÍ∑∏Ïù∏</Link>
            <Link to="/signup" className="nav-button signup">ÌöåÏõêÍ∞ÄÏûÖ</Link>
            <button className="darkmode-toggle" onClick={() => setDarkMode(!darkMode)}>
              {darkMode ? '‚òÄÔ∏è' : 'üåô'}
            </button>
          </div>

          <div className="hamburger" onClick={() => setMenuOpen(!menuOpen)}>
            ‚ò∞
          </div>
        </div>
      </nav>

      {menuOpen && (
        <div className="mobile-menu">
          <Link to="/chatbot" onClick={() => setMenuOpen(false)}>ü§ñ Ï±óÎ¥á</Link>
          <Link to="/image-analysis" onClick={() => setMenuOpen(false)}>üç± Ïù¥ÎØ∏ÏßÄ Î∂ÑÏÑù</Link>
          <Link to="/login" onClick={() => setMenuOpen(false)}>Î°úÍ∑∏Ïù∏</Link>
          <Link to="/signup" onClick={() => setMenuOpen(false)}>ÌöåÏõêÍ∞ÄÏûÖ</Link>
        </div>
      )}
    </>
  );
}
