import React, { useState, useEffect } from 'react';
import axios from 'axios';
import NavBar from './NavBar';
import CalendarView from './CalendarView';
import TableView from './TableView';
import './css/usermealsearch.css';
import './css/main.css';

export default function UserMealSearch() {
  const [query, setQuery] = useState('');
  const [meals, setMeals] = useState([]);
  const [responseType, setResponseType] = useState('table');
  const [filteredMeals, setFilteredMeals] = useState([]);
  const [loading, setLoading] = useState(false);
  const [chatHistory, setChatHistory] = useState([]);
  const BASE_URL = process.env.REACT_APP_BASE_URL;
  const FAST_API_URL = process.env.REACT_APP_FAST_API_URL;

  console.log("üëâ BASE_URL:", process.env.REACT_APP_BASE_URL);
  console.log("üëâ FAST_API_URL:", process.env.REACT_APP_FAST_API_URL);
  useEffect(() => {
    axios.get(`${BASE_URL}/users/meals`, { withCredentials: true })
      .then(res => setMeals(res.data))
      .catch(err => console.error('ÏãùÎã® Ï°∞Ìöå Ïã§Ìå®:', err));
  }, []);

  const handleSubmit = async () => {
    if (!query.trim()) return;

    const userMessage = { role: 'user', content: query };
    setChatHistory(prev => [...prev, userMessage]);

    setLoading(true);
    try {
      console.log('üì§ ÏøºÎ¶¨ Ï†ÑÏÜ°:', query);
      console.log('üì§ meals Ï†ÑÏÜ°:', meals);
      const res = await axios.post(`${FAST_API_URL}/query`, {
        query,
        meals
      }, {
        headers: { 'Content-Type': 'application/json' },
        withCredentials: true
      });

      const result = res.data;
      console.log('üì• ÏùëÎãµ Í≤∞Í≥º:', result);
      setResponseType(result.type);
      setFilteredMeals(result.filteredMeals);

      const aiMessage = {
        role: 'ai',
        content: result.type === 'table'
          ? 'üìã ÏãùÎã®ÏùÑ ÌÖåÏù¥Î∏î ÌòïÏãùÏúºÎ°ú Î≥¥Ïó¨ÎìúÎ¶¥Í≤åÏöî.'
          : 'üìÖ ÏãùÎã®ÏùÑ Îã¨Î†• ÌòïÏãùÏúºÎ°ú Î≥¥Ïó¨ÎìúÎ¶¥Í≤åÏöî.'
      };
      setChatHistory(prev => [...prev, aiMessage]);
    } catch (error) {
      const errorMessage = {
        role: 'ai',
        content: '‚ùå Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§. Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî.'
      };
      setChatHistory(prev => [...prev, errorMessage]);
      console.error('LLM ÏøºÎ¶¨ Ïã§Ìå®:', error);

      if (error.response?.status === 429) {
        alert("OpenAI ÏÇ¨Ïö©ÎüâÏù¥ Ï¥àÍ≥ºÎêòÏóàÏäµÎãàÎã§. Ïû†Ïãú ÌõÑ Îã§Ïãú ÏãúÎèÑÌï¥ Ï£ºÏÑ∏Ïöî.");
      } else {
        alert("ÏöîÏ≤≠ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
      }
    } finally {
      setQuery('');
      setLoading(false);
    }
  };

  return (
    <>
      <NavBar />
      <div className="usermealsearch-wrapper">
        <div className="usermealsearch-inner">
          <div className="chat-feature-panel">
            <div className="visualizer">
              {loading ? (
                <div className="loading-message">‚è≥ Î∂ÑÏÑù Ï§ëÏûÖÎãàÎã§...</div>
              ) : (
                <>
                  {responseType === 'table' && <TableView meals={filteredMeals} />}
                  {responseType === 'calendar' && <CalendarView meals={filteredMeals} />}
                </>
              )}
            </div>
          </div>

          <div className="chatbot-panel">
            <div className="chat-title-bar">
              <span className="chat-icon">üí¨</span>
              <h3 className="chat-title">Ï±óÎ¥á</h3>
            </div>

            <div className="chat-history">
              {chatHistory.map((msg, idx) => (
                <div key={idx} className={`chat-message ${msg.role}`}>
                  <div className="chat-bubble">
                    {msg.content}
                  </div>
                </div>
              ))}
            </div>

            <div className="chatbot-input-area">
              <input
                type="text"
                value={query}
                onChange={e => setQuery(e.target.value)}
                placeholder="Ïòà: 3Ïõî ÏãùÎã®ÏùÑ Îã¨Î†•ÏúºÎ°ú Î≥¥Ïó¨Ï§ò"
                onKeyDown={(e) => e.key === 'Enter' && handleSubmit()}
              />
              <button onClick={handleSubmit} disabled={loading}>
                {loading ? 'Ï†ÑÏÜ° Ï§ë...' : 'Ï†ÑÏÜ°'}
              </button>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}
