import { useEffect, useState } from "react";
import axios from "axios";

function ChatTest() {
  const [userId, setUserId] = useState(null);
  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");
  const [loading, setLoading] = useState(false);

  // ‚úÖ ÏÑ∏ÏÖò Í∏∞Î∞ò Ïú†Ï†Ä ID Î∂àÎü¨Ïò§Í∏∞
  useEffect(() => {
    axios.get("http://localhost:8080/users/user-id", { withCredentials: true })
      .then(res => {
        console.log("üì¶ ÏÑ∏ÏÖò Ïú†Ï†Ä ID:", res.data);
        setUserId(res.data);
      })
      .catch(err => {
        console.error("‚ùå ÏÑ∏ÏÖò Ïú†Ï†Ä Ï†ïÎ≥¥Î•º Î∂àÎü¨Ïò§ÏßÄ Î™ªÌñàÏäµÎãàÎã§.", err.response || err);
      });
  }, []);

  // ‚úÖ ÏßàÎ¨∏ Ï†ÑÏÜ°
  const handleSend = async () => {
    if (!question.trim()) return;
    if (!userId) {
      setAnswer("‚ùó Î°úÍ∑∏Ïù∏Ìïú Ïú†Ï†ÄÎßå ÏßàÎ¨∏Ìï† Ïàò ÏûàÏäµÎãàÎã§.");
      return;
    }

    setLoading(true);
    try {
      console.log("üì® Ï†ÑÏÜ°Ìï† userId:", userId);
      const res = await axios.post("http://localhost:8000/chat/chat", {
        user_id: userId,
        question,
      });
      setAnswer(res.data.answer);
    } catch (err) {
      console.error("‚ùå FastAPI ÏóêÎü¨:", err.response || err);
      setAnswer(`‚ö†Ô∏è ÏÑúÎ≤Ñ Ïò§Î•ò: ${err.response?.data?.detail || err.message}`);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="chat-container" style={{ padding: "2rem", fontFamily: "sans-serif" }}>
      <h2>üó®Ô∏è Gemini Ï±óÎ¥á</h2>
      {!userId ? (
        <p>üîê Î°úÍ∑∏Ïù∏ Ïú†Ï†Ä Ï†ïÎ≥¥Î•º Î∂àÎü¨Ïò§Îäî Ï§ëÏûÖÎãàÎã§...</p>
      ) : (
        <>
          <textarea
            value={question}
            onChange={(e) => setQuestion(e.target.value)}
            placeholder="ÏßàÎ¨∏ÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
            rows="5"
            cols="50"
            style={{ padding: "1rem", fontSize: "1rem" }}
          />
          <br />
          <button
            onClick={handleSend}
            disabled={loading}
            style={{ marginTop: "0.5rem", padding: "0.5rem 1rem", fontSize: "1rem" }}
          >
            {loading ? "ÏßàÎ¨∏ Ï†ÑÏÜ° Ï§ë..." : "ÏßàÎ¨∏ Î≥¥ÎÇ¥Í∏∞"}
          </button>
          <div style={{ marginTop: "1.5rem" }}>
            <strong>üí¨ ÏùëÎãµ:</strong>
            <div style={{ whiteSpace: "pre-wrap", marginTop: "0.5rem", lineHeight: "1.7" }}>
              {answer}
            </div>
          </div>
        </>
      )}
    </div>
  );
}

export default ChatTest;
