import React, { useEffect } from 'react';
import './css/kakaomap.css';

export default function KakaoMap({ stores }) {
  console.log("üì¶ Ï†ÑÎã¨Îêú stores:", stores);
  stores.forEach(store => console.log("üìç Ï£ºÏÜå ÌôïÏù∏:", store.real_address));

  useEffect(() => {
    const loadKakaoMapScript = () => {
      if (window.kakao && window.kakao.maps) {
        console.log("‚úÖ Ïù¥ÎØ∏ kakao Í∞ùÏ≤¥ ÏûàÏùå");
        window.kakao.maps.load(initMap);
        return;
      }

      const script = document.createElement("script");
      script.src =
        "//dapi.kakao.com/v2/maps/sdk.js?appkey=cdcb36f7c5dbf63f04c19c92959118ca&autoload=false&libraries=services";
      script.async = true;

      script.onload = () => {
        console.log("‚úÖ Kakao SDK Ïä§ÌÅ¨Î¶ΩÌä∏ Î°úÎî© ÏôÑÎ£å");
        window.kakao.maps.load(() => {
          console.log("‚úÖ kakao.maps.load ÏôÑÎ£åÎê®", window.kakao);
          initMap();
        });
      };

      script.onerror = () => {
        console.error("‚ùå Kakao SDK Î°úÎî© Ïã§Ìå® - ÌÇ§ ÎòêÎäî ÎèÑÎ©îÏù∏ ÌôïÏù∏ ÌïÑÏöî");
      };

      document.head.appendChild(script);
    };

    const initMap = () => {
      if (!stores || stores.length === 0) return;

      const filteredStores = stores.filter(
        (store) => store.real_address && store.real_address.trim().length > 5
      );

      console.log("üó∫Ô∏è ÌïÑÌÑ∞Îêú stores (Ï£ºÏÜå ÏûàÎäî Í≤ÉÎßå):", filteredStores);
      if (filteredStores.length === 0) return;

      const container = document.getElementById("map");
      const options = {
        center: new window.kakao.maps.LatLng(37.5665, 126.9780), // Ï¥àÍ∏∞ Ï§ëÏã¨ (ÏÑúÏö∏ ÏãúÏ≤≠)
        level: 5,
      };
      const map = new window.kakao.maps.Map(container, options);
      const geocoder = new window.kakao.maps.services.Geocoder();

      const bounds = new window.kakao.maps.LatLngBounds(); // ‚úÖ Î™®Îì† ÎßàÏª§ Î≤îÏúÑ

      let markerCount = 0;

      filteredStores.forEach((store) => {
        geocoder.addressSearch(store.real_address, function (result, status) {
          console.log(
            `üìç Ï£ºÏÜå Í≤ÄÏÉâ Í≤∞Í≥º [${store.name}]`,
            store.real_address,
            status,
            result
          );

          if (status === window.kakao.maps.services.Status.OK) {
            const coords = new window.kakao.maps.LatLng(result[0].y, result[0].x);
            bounds.extend(coords); // ‚úÖ ÎßàÏª§ Î≤îÏúÑ Ï∂îÍ∞Ä

            const marker = new window.kakao.maps.Marker({
              map: map,
              position: coords,
            });

            const info = new window.kakao.maps.InfoWindow({
              content: `<div style="padding:5px;">${store.name}</div>`,
            });

            marker.setMap(map);
            window.kakao.maps.event.addListener(marker, "click", () => {
              info.open(map, marker);
            });

            markerCount++;
            // ‚úÖ Î™®Îì† ÎßàÏª§ ÏÉùÏÑ± ÌõÑ ÏßÄÎèÑ Î≤îÏúÑ ÏÑ§Ï†ï
            if (markerCount === filteredStores.length) {
              map.setBounds(bounds);
            }
          }
        });
      });
    };

    loadKakaoMapScript();
  }, [stores]);

  return (
    <div
      id="map"
      style={{ width: "100%", height: "400px", marginTop: "20px" }}
    />
  );
}
