import React, { useEffect, useState } from 'react';
import './css/admin.css';

export default function AdminPage() {
  const [users, setUsers] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const usersPerPage = 10;

  // 실제 API 연동 시 fetch/axios로 대체
  useEffect(() => {
    fetch('/api/admin/users') // 예시 엔드포인트
      .then((res) => res.json())
      .then((data) => setUsers(data))
      .catch((err) => console.error('유저 데이터 불러오기 실패:', err));
  }, []);

  const handleRoleChange = (id, newRole) => {
    const updated = users.map(user =>
      user.id === id ? { ...user, role: newRole } : user
    );
    setUsers(updated);

    // 실제 요청
    fetch(`/api/admin/users/${id}/role`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ role: newRole }),
    });
  };

  const handleDelete = (id) => {
    if (window.confirm('정말 삭제하시겠습니까?')) {
      setUsers(users.filter(user => user.id !== id));
      fetch(`/api/admin/users/${id}`, { method: 'DELETE' });
    }
  };

  // Pagination
  const indexOfLastUser = currentPage * usersPerPage;
  const indexOfFirstUser = indexOfLastUser - usersPerPage;
  const currentUsers = users.slice(indexOfFirstUser, indexOfLastUser);
  const totalPages = Math.ceil(users.length / usersPerPage);

  return (
    <div className="container">
      <h2 className="section-title">회원 목록</h2>

      <table className="admin-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>이메일</th>
            <th>등급</th>
            <th>가입 날짜</th>
            <th>관리</th>
          </tr>
        </thead>
        <tbody>
          {currentUsers.map(user => (
            <tr key={user.id}>
              <td>{user.id}</td>
              <td>{user.email}</td>
              <td>
                <select
                  value={user.role}
                  onChange={(e) => handleRoleChange(user.id, e.target.value)}
                  className="dropdown-select"
                >
                  <option value="user">기본</option>
                  <option value="premium">프리미엄</option>
                </select>
              </td>
              <td>{user.joinDate}</td>
              <td>
                <button className="btn btn-danger" onClick={() => handleDelete(user.id)}>
                  삭제
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Pagination */}
      <div className="pagination">
        {Array.from({ length: totalPages }, (_, i) => i + 1).map((num) => (
          <button
            key={num}
            onClick={() => setCurrentPage(num)}
            className={`page-button ${currentPage === num ? 'active' : ''}`}
          >
            {num}
          </button>
        ))}
      </div>
    </div>
  );
}
