import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // üëà ÌéòÏù¥ÏßÄ Ïù¥ÎèôÏùÑ ÏúÑÌï¥ Ï∂îÍ∞Ä
import './css/login.css';

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate(); // üëà ÌéòÏù¥ÏßÄ Ïù¥Îèô Ìï®Ïàò

  const handleLogin = async (e) => {
    e.preventDefault();
  
    try {
      const response = await axios.post(
        'http://localhost:8080/users/loginUser', 
        new URLSearchParams({
          email: username,
          user_password: password,
        }),
        {
          headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
          withCredentials: true,
        }
      );

      if (response.status === 200) {
        alert('Î°úÍ∑∏Ïù∏ ÏÑ±Í≥µ!');
        console.log(response.data); // üëà Ïú†Ï†Ä Ï†ïÎ≥¥ ÌôïÏù∏

        // Ïú†Ï†Ä Ï†ïÎ≥¥ Ï†ÄÏû• (Ïòà: localStorage)
        localStorage.setItem('user', JSON.stringify(response.data));

        // ÌéòÏù¥ÏßÄ Ïù¥Îèô
        navigate('/myPage');
      }
    } catch (err) {
      setError('Î°úÍ∑∏Ïù∏ Ïã§Ìå®: ÏïÑÏù¥Îîî ÎòêÎäî ÎπÑÎ∞ÄÎ≤àÌò∏Î•º ÌôïÏù∏ÌïòÏÑ∏Ïöî.');
      console.error(err);
    }
  };

  return (
    <form onSubmit={handleLogin}>
      <div>
        <label>ÏïÑÏù¥Îîî</label>
        <input
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />
      </div>
      <div>
        <label>ÎπÑÎ∞ÄÎ≤àÌò∏</label>
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
      </div>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <button type="submit">Î°úÍ∑∏Ïù∏</button>
    </form>
  );
}

export default Login;
