import React, { useState } from 'react';
import axios from 'axios';
import NavBar from './NavBar';
import { toast } from 'react-toastify';
import './css/meal-recommend.css';
import './css/mypage.css';
import './css/main.css';
import { useParams } from 'react-router-dom';

export default function MealRecommendation() {
  const { foodName } = useParams();
  const [location, setLocation] = useState(''); // ✅ 빈 문자열로 초기화
  const [recommendedStore, setRecommendedStore] = useState([]);
  const [loading, setLoading] = useState(false);

  const handleRecommend = async () => {
    if (!location.trim()) {
      toast.error('주소를 입력해주세요!');
      return;
    }

    try {
      setLoading(true);

      const payload = new URLSearchParams({
        foodName: foodName,
        location: location,
      });

      const response = await axios.post('http://localhost:8080/analyze/store', payload, {
        headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
        withCredentials: true,
      });

      if (response.status === 200) {
        toast.success('추천 완료!');
      }

      console.log('📦 받은 추천 결과:', response.data); // ✅ 디버깅용 로그
      setRecommendedStore(response.data);
    } catch (error) {
      console.error('추천 실패:', error);
      toast.error('추천 요청 중 오류가 발생했습니다.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      <NavBar />
      <div className="recommend-wrapper">
        <div className="recommend-inner">
          <h2 className="title">📅 {foodName} 맞춤 식당 추천</h2>
          <input
            placeholder="주소를 입력하세요!"
            value={location}
            onChange={(e) => setLocation(e.target.value)}
          />
          <button onClick={handleRecommend} className="btn btn-primary" disabled={loading}>
            {loading ? '🍽 추천중...' : '🍱 추천 받기'}
          </button>
          {loading && (
            <p style={{ marginTop: '10px', fontWeight: '500', color: '#555' }}>
              추천 결과를 생성 중입니다. 잠시만 기다려주세요...
            </p>
          )}

        <div className="table-scroll-container">
          <table className="result-table">
            <thead>
              <tr>
                <th className="col-narrow">상호명</th>
                <th className="col-narrow">링크</th>
              </tr>
            </thead>
            <tbody>
              {recommendedStore.length === 0 ? (
                <tr>
                  <td colSpan="2">추천된 식당이 없습니다.</td>
                </tr>
              ) : (
                recommendedStore.map((store, index) => (
                  <tr key={index}>
                    <td>{store?.name || '이름 없음'}</td>
                    <td>
                      {store?.address ? (
                        <a href={store.address} target="_blank" rel="noopener noreferrer">
                          링크 열기
                        </a>
                      ) : (
                        '링크 없음'
                      )}
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
          </div>
        </div>
      </div>
    </>
  );
}
