import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './css/main.css';

export default function MainPage() {
  const [userInfo, setUserInfo] = useState(null);
  const [menuOpen, setMenuOpen] = useState(false);
  const [darkMode, setDarkMode] = useState(false);

  useEffect(() => {
    const storedUser = localStorage.getItem('user');
      if (storedUser) {
          setUserInfo(JSON.parse(storedUser));
      }
    }, []);

  useEffect(() => {
    document.documentElement.setAttribute('data-theme', darkMode ? 'dark' : 'light');
  }, [darkMode]);

  useEffect(() => {
    if (menuOpen) {
      document.body.style.overflow = 'hidden';
    } else {
      document.body.style.overflow = '';
    }
  }, [menuOpen]);

  const handleLogout = async () => {
    try {
        await axios.post('http://localhost:8080/users/logout', 
            new URLSearchParams({
              user_id: userInfo.user_id,
            }),
            {
              headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
              withCredentials: true,
            }
        );

        // Î°úÍ∑∏ÏïÑÏõÉ ÏÑ±Í≥µ Ïãú Ï≤òÎ¶¨
        localStorage.removeItem('user');
        navigate('/login');
    } catch (err) {
        console.error('Î°úÍ∑∏ÏïÑÏõÉ Ïã§Ìå®:', err);
        alert('Î°úÍ∑∏ÏïÑÏõÉ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
    }
};

  return (
    <>
      <nav className="glass-navbar">
        <div className="navbar-container">
          <div className="navbar-left">
            <Link to="/" className="logo">üçΩ <span>FoodyChat</span></Link>
          </div>

          <div className="navbar-center desktop-menu">
            <Link to="/chatbot" className="nav-link">ü§ñ <span>Ï±óÎ¥á</span></Link>
            <Link to="/image-analysis" className="nav-link">üç± <span>Ïù¥ÎØ∏ÏßÄ Î∂ÑÏÑù</span></Link>
            {userInfo && (
            <>
              {userInfo.membership_lvl != 'admin' && (
                <Link to="/mypage" className="nav-link">üç± <span>ÎßàÏù¥ÌéòÏù¥ÏßÄ</span></Link>
              )}
              {userInfo.membership_lvl === 'admin' && (
                <Link to="/adminpage" className="nav-link">üç± <span>Í¥ÄÎ¶¨ÏûêÌéòÏù¥ÏßÄ</span></Link>
              )}
              <button onClick={handleLogout} className="nav-button logout">
              {userInfo.user_id}Îãò ÌôòÏòÅÌï©ÎãàÎã§
              </button>
            </>
            )}
          </div>

          <div className="navbar-right desktop-menu">
            {!userInfo && (
              <>
                <Link to="/login" className="nav-button">Î°úÍ∑∏Ïù∏</Link>
                <Link to="/signup" className="nav-button signup">ÌöåÏõêÍ∞ÄÏûÖ</Link>
              </>
            )}
            <button
              className="darkmode-toggle"
              onClick={() => setDarkMode(!darkMode)}
              aria-label="Dark mode toggle"
            >
              {darkMode ? '‚òÄÔ∏è' : 'üåô'}
            </button>
          </div>

          <div
            className={`hamburger ${menuOpen ? 'open' : ''}`}
            onClick={() => setMenuOpen(!menuOpen)}
            aria-label="Menu toggle"
          >
            <span />
            <span />
            <span />
          </div>
        </div>
      </nav>

      <div className={`mobile-menu ${menuOpen ? 'show' : ''}`}>
        <Link to="/chatbot" onClick={() => setMenuOpen(false)}>ü§ñ Ï±óÎ¥á</Link>
        <Link to="/image-analysis" onClick={() => setMenuOpen(false)}>üç± Ïù¥ÎØ∏ÏßÄ Î∂ÑÏÑù</Link>
        {userInfo && (
          <>
            {userInfo.membership_lvl != 'admin' && (
              <Link to="/mypage" onClick={() => setMenuOpen(false)}>üç± ÎßàÏù¥ÌéòÏù¥ÏßÄ</Link>
            )}
            {userInfo.membership_lvl === 'admin' && (
              <Link to="/adminpage" onClick={() => setMenuOpen(false)}>üç± Í¥ÄÎ¶¨ÏûêÌéòÏù¥ÏßÄ</Link>
            )}
            <button onClick={() => { setMenuOpen(false); handleLogout(); }} className="mobile-logout-button">
              Î°úÍ∑∏ÏïÑÏõÉ
            </button>
          </>
        )}
        
        {!userInfo && (
          <>
            <Link to="/login" onClick={() => setMenuOpen(false)}>Î°úÍ∑∏Ïù∏</Link>
            <Link to="/signup" onClick={() => setMenuOpen(false)}>ÌöåÏõêÍ∞ÄÏûÖ</Link>
          </>
        )}
      </div>
    </>
  );
}
