import { useEffect, useState } from "react";
import axios from "axios";

function ChatTest() {
  const [userId, setUserId] = useState(null);
  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");
  const [loading, setLoading] = useState(false);

  // ‚úÖ 2Îã®Í≥Ñ: ÏÑ∏ÏÖò Í∏∞Î∞ò Ïú†Ï†Ä Ï†ïÎ≥¥ Í∞ÄÏ†∏Ïò§Í∏∞
  useEffect(() => {
    axios.get("http://localhost:8080/users/user-id", { withCredentials: true })
      .then(res => {
        console.log("üì¶ /user-id ÏùëÎãµ Îç∞Ïù¥ÌÑ∞:", res.data);
        setUserId(res.data);  // user_id Í∞í ÌôïÏù∏ ÌõÑ ÏÉÅÌÉú ÏÑ§Ï†ï
      })
      .catch(err => {
        console.error("ÏÑ∏ÏÖò Ïú†Ï†Ä Ï†ïÎ≥¥Î•º Î∂àÎü¨Ïò§ÏßÄ Î™ªÌñàÏäµÎãàÎã§.", err.response || err);
      });
  }, []);

  // ‚úÖ 3Îã®Í≥Ñ: ÏßàÎ¨∏ Ï†ÑÏÜ°
  const handleSend = async () => {
    if (!question.trim() || !userId) return;
    setLoading(true);
    try {
      console.log("Ï†ÑÏÜ°Ìï† userId:", userId);
      const res = await axios.post("http://localhost:8000/chat/chat", {
        user_id: userId,
        question,
      });
      setAnswer(res.data.answer);  // Gemini ÏùëÎãµÏùÑ answer ÏÉÅÌÉúÏóê ÏÑ§Ï†ï
    } catch (err) {
      console.error("FastAPI ÏóêÎü¨:", err.response || err);  // ÏÑúÎ≤Ñ ÏóêÎü¨ ÎîîÎ≤ÑÍπÖ
      setAnswer(`‚ö†Ô∏è ÏÑúÎ≤Ñ Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§: ${err.response?.data?.detail || err.message}`);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="chat-container">
      <h2>üó®Ô∏è Gemini Ï±óÎ¥á</h2>
      {!userId ? (
        <p>üîê Î°úÍ∑∏Ïù∏ Ïú†Ï†Ä Ï†ïÎ≥¥Î•º Î∂àÎü¨Ïò§Îäî Ï§ëÏûÖÎãàÎã§...</p>
      ) : (
        <>
          <textarea
            value={question}
            onChange={(e) => setQuestion(e.target.value)}
            placeholder="ÏßàÎ¨∏ÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
            rows="5"
            cols="50"
          />
          <button onClick={handleSend} disabled={loading}>
            ÏßàÎ¨∏ Î≥¥ÎÇ¥Í∏∞
          </button>
          {loading ? <p>ÎãµÎ≥Ä ÏÉùÏÑ± Ï§ë...</p> : <p><strong>ÏùëÎãµ:</strong> {answer}</p>}
        </>
      )}
    </div>
  );
}

export default ChatTest;
