import React, { useState, useRef } from 'react';
import axios from 'axios';
import NavBar from './NavBar';
import { useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';
import './css/main.css';
import './css/image-analysis.css';

export default function Imageanalysis() {
  const [selectedImage, setSelectedImage] = useState(null);
  const [previewUrl, setPreviewUrl] = useState(null);
  const [analysisResult, setAnalysisResult] = useState(null);
  const imageInputRef = useRef();
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();
  const BASE_URL = process.env.REACT_APP_BASE_URL;

  const handleImageChange = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    setSelectedImage(file);
    setPreviewUrl(URL.createObjectURL(file));
    setAnalysisResult(null);
    setLoading(true);

    try {
      const formData = new FormData();
      formData.append('file', file);

      const response = await axios.post(`${BASE_URL}/analyze/upload`, formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
        withCredentials: true,
      });

      console.log("üçΩ Í≤∞Í≥º:", response.data);
      setAnalysisResult(response.data);
    } catch (error) {
      // Î°úÍ∑∏Ïù∏ ÌïÑÏöîÏóê Îî∞Î•∏ 401 Ï≤òÎ¶¨
      if (error.response?.status === 401) {
        setPreviewUrl(null);
        setSelectedImage(null);
        if (imageInputRef.current) imageInputRef.current.value = '';
        toast.info(error.response.data || 'Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.');
      } else {
        toast.error('Î∂ÑÏÑù Ïã§Ìå®');
      }
      console.error('‚ùå ÏóêÎü¨:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleRecommendation = () => {
    if (analysisResult?.predictedClass) {
      navigate(`/cafe-recommend/${analysisResult.predictedClass}`);
    } else {
      if (window.confirm("ÏòàÏ∏°Îêú ÏùåÏãùÏù¥ ÏóÜÏäµÎãàÎã§. Ï†ÑÏ≤¥ ÏãùÎãπ Î™©Î°ùÏùÑ Î≥¥ÏãúÍ≤†ÏäµÎãàÍπå?")) {
        navigate('/cafe-recommend/all');
      } else {
        toast.info("Î∂ÑÏÑù ÌõÑ Ï∂îÏ≤úÏùÑ Ïù¥Ïö©Ìï¥Î≥¥ÏÑ∏Ïöî.");
      }
    }
  };

  return (
    <>
      <NavBar />
      <div className="analysis-wrapper">
        <div className="analysis-inner">
          <h1 className="analysis-title">Ïù¥ÎØ∏ÏßÄ Î∂ÑÏÑù</h1>

          {/* Ïù¥ÎØ∏ÏßÄ ÏóÖÎ°úÎìú ÏòÅÏó≠ */}
          {!previewUrl && (
            <div
              className="upload-area"
              onClick={() => imageInputRef.current.click()}
              onDragOver={(e) => e.preventDefault()}
              onDrop={(e) => {
                e.preventDefault();
                const file = e.dataTransfer.files[0];
                if (file) {
                  imageInputRef.current.files = e.dataTransfer.files;
                  handleImageChange({ target: { files: e.dataTransfer.files } });
                }
              }}
            >
              <p>Ïù¥ÎØ∏ÏßÄÎ•º ÌÅ¥Î¶≠ÌïòÍ±∞ÎÇò ÎìúÎûòÍ∑∏ Ïï§ ÎìúÎ°≠ÌïòÏÑ∏Ïöî</p>
              <input
                type="file"
                accept="image/*"
                ref={imageInputRef}
                onChange={handleImageChange}
                style={{ display: 'none' }}
              />
            </div>
          )}

          {/* üñºÔ∏è Ïù¥ÎØ∏ÏßÄ ÎØ∏Î¶¨Î≥¥Í∏∞ */}
          {previewUrl && (
            <div
              className="image-preview"
              onClick={() => imageInputRef.current.click()}
              style={{ cursor: 'pointer' }}
            >
              <img src={previewUrl} alt="ÏóÖÎ°úÎìúÎêú ÏùåÏãù" />
              <input
                type="file"
                accept="image/*"
                ref={imageInputRef}
                onChange={handleImageChange}
                style={{ display: 'none' }}
              />
            </div>
          )}

          {/* Î∂ÑÏÑù Ï§ë Î©îÏãúÏßÄ */}
          {loading && <p className="loading-message">üîç Î∂ÑÏÑù Ï§ëÏûÖÎãàÎã§...</p>}

          {analysisResult && (
            <>
              <div className="result-card">
                <div className="predicted-class">
                  üçΩÔ∏è ÏòàÏ∏° Í≤∞Í≥º: <strong>{analysisResult.food_ko_name}</strong>
                </div>
                <div className="confidence-score">
                  Ïã†Î¢∞ÎèÑ: {(analysisResult.confidence * 100).toFixed(2)}%
                </div>
              </div>

              {/* ‚úÖ ÏòÅÏñëÏ†ïÎ≥¥ Ïπ¥Îìú */}
              <div className="nutrition-card">
                <h3>ü•ó ÏòÅÏñë Ï†ïÎ≥¥</h3>
                <ul className="nutrition-list">
                  <li><strong>ÏπºÎ°úÎ¶¨:</strong> {analysisResult.calories} kcal</li>
                  <li><strong>ÌÉÑÏàòÌôîÎ¨º:</strong> {analysisResult.nut_carb} g</li>
                  <li><strong>Îã®Î∞±Ïßà:</strong> {analysisResult.nut_pro} g</li>
                  <li><strong>ÏßÄÎ∞©:</strong> {analysisResult.nut_fat} g</li>
                </ul>
              </div>
            </>
          )}

          {/* Ï∂îÏ≤ú Î≤ÑÌäº */}
          <div className="button-group">
            <button
              onClick={handleRecommendation}
              className="button secondary btn"
            >
              ÏãùÎãπ Ï∂îÏ≤ú
            </button>
            <button
              onClick={() => navigate('/meal-recommend')}
              className="button secondary btn"
            >
              ÏãùÎã® Ï∂îÏ≤ú
            </button>
          </div>
        </div>
      </div>
    </>
  );
}
