import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const ChatTest = () => {
  const [user, setUser] = useState(null); // ÏÑ∏ÏÖò Ïú†Ï†Ä
  const [message, setMessage] = useState("");
  const [responses, setResponses] = useState([]);
  const navigate = useNavigate();

  // ÏÑ∏ÏÖò ÌôïÏù∏
  useEffect(() => {
    const fetchSession = async () => {
      try {
        const res = await axios.get("http://localhost:8080/ses", {
          withCredentials: true,
        });

        if (res.data && res.data.user_id) {
          setUser(res.data); // Ïú†Ï†Ä Ï†ïÎ≥¥ Ï†ÄÏû•
        } else {
          navigate("/login"); // ÏÑ∏ÏÖò ÏóÜÏúºÎ©¥ Î°úÍ∑∏Ïù∏ ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô
        }
      } catch (err) {
        console.error("ÏÑ∏ÏÖò ÌôïÏù∏ Ïã§Ìå®:", err);
        navigate("/login");
      }
    };

    fetchSession();
  }, [navigate]);

  const handleSend = async () => {
    if (!message.trim()) return;

    try {
      const res = await axios.post(
        "http://localhost:8000/api/rag/generate",
        { question: message },
        { withCredentials: true }
      );

      setResponses((prev) => [
        ...prev,
        { role: "user", content: message },
        { role: "bot", content: res.data.answer },
      ]);
      setMessage("");
    } catch (err) {
      console.error("Ï±óÎ¥á ÏùëÎãµ Ïã§Ìå®:", err);
    }
  };

  if (!user) return <div className="text-center mt-10">ÏÑ∏ÏÖò ÌôïÏù∏ Ï§ë...</div>;

  return (
    <div className="max-w-xl mx-auto p-4">
      <h2 className="text-xl font-semibold mb-4">ÏïàÎÖïÌïòÏÑ∏Ïöî, {user.nickname}Îãò üëã</h2>
      <div className="border p-4 rounded h-96 overflow-y-scroll mb-2 bg-gray-100">
        {responses.map((msg, idx) => (
          <div
            key={idx}
            className={`mb-2 ${
              msg.role === "user" ? "text-right" : "text-left"
            }`}
          >
            <span
              className={`inline-block px-3 py-1 rounded ${
                msg.role === "user" ? "bg-blue-200" : "bg-green-200"
              }`}
            >
              {msg.content}
            </span>
          </div>
        ))}
      </div>
      <div className="flex gap-2">
        <input
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Î©îÏãúÏßÄÎ•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî..."
          className="flex-grow border px-3 py-2 rounded"
        />
        <button
          onClick={handleSend}
          className="bg-blue-500 text-white px-4 py-2 rounded"
        >
          Ï†ÑÏÜ°
        </button>
      </div>
    </div>
  );
};

export default ChatTest;
